import React, { useEffect, useState } from "react";
import { StatusBar } from 'expo-status-bar';
import { FlatList, RefreshControl } from 'react-native';
import { Container, Content, BackContainer, BackContent, TopContainer, SearchInput, 
  ContainerSearch, Filter, DataContent, DataLab, DataReplyLab, DataLimiter, IconsContent, IconContent } from "./styles";
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome';
import { faRightFromBracket, faHouse, faArrowUpAZ, faMagnifyingGlass, faEye, faPen } from "@fortawesome/free-solid-svg-icons";

const Cliente = ({ navigation }) => {
  
  const [refreshing, setRefreshing] = React.useState(false);
  const [dados, setDados] = useState([]);
  const [searchText, setSearchText] = useState('');

  useEffect(() => {  
      setRefreshing(true);
      getData();
  }, [searchText]); 

  const getData = () => {
    fetch('http://192.168.18.50/SmartMobile_Api/public/api/C000007', {
      method: 'GET',
      headers: {
        'Accept': 'application/json'
      }
    })
       .then(response => response.json())
       .then(data => {
        
        if(searchText === '') {
          setRefreshing(true);
          setDados(data);
          setRefreshing(false);
         } else {
          setRefreshing(true);
           setDados(
             data.filter(
               (fil) => 
                fil.CPF.indexOf(searchText) > -1 || fil.NOME.toLowerCase().indexOf(searchText.toLowerCase()) > -1 
             )
           );
           setRefreshing(false);
             }
       })
    };

      const handleOrderClick = () => {
        setRefreshing(true);
        let newList = [...dados];
        newList.sort((a, b) => (a.NOME > b.NOME ? 1 : b.NOME > a.NOME ? -1 : 0));   
        setDados(newList);
        setRefreshing(false);
      };     

      const onRefresh = (data) => {
        setRefreshing(true);
        setDados(data);
        getData();

      };

      function renderItem({ item }){
        return <DataContent>
               
            <DataLimiter>
            <DataLab> Codigo: <DataReplyLab> {item.CODIGO} </DataReplyLab> </DataLab>
            <DataLab> Cliente: <DataReplyLab> {item.NOME} {item.APELIDO} </DataReplyLab> </DataLab>
            <DataLab> CPF: <DataReplyLab> {item.CPF} </DataReplyLab> </DataLab>
            </DataLimiter>

            <IconsContent>
            <IconContent onPress={() => navigation.navigate('ShowCliente', {
            cod: item.CODIGO,
            nome: item.NOME,
            apelido: item.APELIDO,
            numero: item.NUMERO,
            endereco: item.ENDERECO,
            bairro: item.BAIRRO,
            cidade: item.CIDADE,
            uf: item.UF,
            cep: item.CEP,
            complemento: item.COMPLEMENTO,
            tipo: item.TIPO,
            situacao: item.SITUACAO,
            telefone1: item.TELEFONE1,
            telefone2: item.TELEFONE2,
            telefone3: item.TELEFONE3,
            celular: item.CELULAR,
            email: item.EMAIL,
            rg: item.RG,
            cpf: item.CPF,
            data_cadastro: item.DATA_CADASTRO,
            obs1: item.OBS1,
            obs2: item.OBS2,
            obs3: item.OBS3,
            obs4: item.OBS4,
            obs5: item.OBS5,
            obs6: item.OBS6,
            consumidor_final: item.CONSUMIDOR_FINAL,
            atb: item.ATB, })}>
            <FontAwesomeIcon icon={faEye} size={ 21 } color={ '#dbdbdb' } />  
            </IconContent>
            <IconContent onPress={() => navigation.navigate('EditCliente', {
            cod: item.CODIGO,
            nome: item.NOME,
            apelido: item.APELIDO,
            numero: item.NUMERO,
            endereco: item.ENDERECO,
            bairro: item.BAIRRO,
            cidade: item.CIDADE,
            uf: item.UF,
            cep: item.CEP,
            complemento: item.COMPLEMENTO,
            tipo: item.TIPO,
            situacao: item.SITUACAO,
            telefone1: item.TELEFONE1,
            telefone2: item.TELEFONE2,
            telefone3: item.TELEFONE3,
            celular: item.CELULAR,
            email: item.EMAIL,
            rg: item.RG,
            cpf: item.CPF,
            data_cadastro: item.DATA_CADASTRO,
            obs1: item.OBS1,
            obs2: item.OBS2,
            obs3: item.OBS3,
            obs4: item.OBS4,
            obs5: item.OBS5,
            obs6: item.OBS6,
            consumidor_final: item.CONSUMIDOR_FINAL,
            atb: item.ATB, })}>
            <FontAwesomeIcon icon={faPen} size={ 15 } color={ '#dbdbdb' } />  
            </IconContent>
            </IconsContent>

            </DataContent>;
      }

    return (
        <Container>
          <StatusBar backgroundColor="#677689"/>         
          <TopContainer>
          <ContainerSearch>
          <FontAwesomeIcon icon={faMagnifyingGlass} size={ 25 } color={ '#677689' }/>
          </ContainerSearch>                       
          <SearchInput
          placeholder='Buscar Cliente'
          name={searchText}
          onChangeText={(t) => setSearchText(t)}/>
          <Filter onPress={handleOrderClick}>                               
          <FontAwesomeIcon icon={faArrowUpAZ}  size={ 25 } color={ '#677689' }/>
          </Filter>                             
          </TopContainer>
            
          <Content Style={{alignItems: 'center' }}>

            <FlatList
            data={dados}            
            keyExtractor={item => item.CODIGO}
            refreshControl={ <RefreshControl refreshing={refreshing}  onRefresh={onRefresh} /> }
            renderItem={renderItem}/>
          
          </Content> 

          <BackContainer>
            <BackContent onPress={() => navigation.navigate('Home')}>             
            <FontAwesomeIcon icon={faHouse} size={ 52 } color={ '#677689' }/>
            </BackContent>
            <BackContent onPress={() => navigation.navigate('Login')}>             
            <FontAwesomeIcon icon={faRightFromBracket} size={ 52 } color={ '#677689' }/>
            </BackContent>
          </BackContainer>
          
        </Container>
    )
  }

  export default Cliente;
