import { Alert } from 'react-native';
import React, { useEffect, useState } from "react";
import { Container, Form, ContainerInput, Input, ContainerLab, InputLab, Save, SaveLab, ErrorMensage } from "./styles/form";
import { useForm, Controller } from "react-hook-form";
import {Picker} from '@react-native-picker/picker';
import { yupResolver } from '@hookform/resolvers/yup'
import * as yup from 'yup'

const CreateCliente = ({navigation}) => {

  const [selectedTipo, setSelectedTipo] = useState();
  const [selectedUF, setSelectedUF] = useState();
  const date = new Date();
  const data_cadastro = date.getDate() + '.0' + (date.getMonth() + 1 ) + '.' + date.getFullYear();

      const schema = yup.object({
        nome: yup.string().required("Por favor preencha esse campo"),
        apelido: yup.string().required("Por favor preencha esse campo")
      })

      const { control, handleSubmit, formState: { errors } } = useForm({
      
          resolver: yupResolver(schema),
    
      })

      let handleEdit = async (data) => {

          fetch(`http://192.168.18.35/SmartMobile_Api/public/api/C000007`, {
          method: 'POST',
          body: JSON.stringify({
            NOME: data.nome,
            APELIDO: data.apelido,
            NUMERO: data.numero,
            ENDERECO: data.endereço,
            BAIRRO: data.bairro,
            CIDADE: data.cidade,
            UF: selectedUF,
            CEP: data.cep,
            COMPLEMENTO: data.complemento,
            TELEFONE1: data.telefone1,
            TELEFONE2: data.telefone2,
            TELEFONE3: data.telefone3,
            CELULAR: data.celular,
            EMAIL: data.email,
            RG: data.rg,
            CPF: data.cpf,
            OBS1: data.obs1,
            OBS2: data.obs2,
            OBS3: data.obs3,
            OBS4: data.obs4,
            OBS5: data.obs5,
            OBS6: data.obs6,
            DATA_CADASTRO: data_cadastro,
            TIPO: selectedTipo,
            SITUACAO: '1',
            CONSUMIDOR_FINAL: 'S',
            ATB: '000001'
          }),
          headers: {
            'Content-type': 'application/json; charset=UTF-8',
          },
        })
          .then((response) => response.json())
          .then((json) => console.log(json));
          Alert.alert(
            "Cadastro bem sucedido",
            'O cliente foi cadastro com sucesso.',
            [ 
              { text: "Voltar", onPress: () => navigation.navigate('Cliente') }
            ]
          );
      }

    return (
        <Container>
          <Form contentContainerStyle={{ alignItems: 'center' }}>

          <Controller
          control={control}
          name="nome"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> Nome </InputLab>
          </ContainerLab>
            <Input
            onChangeText={onChange}
            OnBlur={OnBlur}
            value={value}
            placeholder='Nome do cliente'/>
            </ContainerInput>
          )} />

          {errors.nome && <ErrorMensage> {errors.nome?.message} </ErrorMensage>}

                    <Controller
          control={control}
          name="apelido"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> Apelido </InputLab>
          </ContainerLab>
            <Input
            onChangeText={onChange}
            OnBlur={OnBlur}
            value={value}
            placeholder='apelido do cliente'/>
            </ContainerInput>
          )} />

          {errors.apelido && <ErrorMensage> {errors.apelido?.message} </ErrorMensage>}

                    <Controller
          control={control}
          name="numero"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> Numero </InputLab>
          </ContainerLab>
            <Input
            onChangeText={onChange}
            OnBlur={OnBlur}
            value={value}
            placeholder='numero do cliente'/>
            </ContainerInput>
          )} />

          <Controller
          control={control}
          name="endereco"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> Endereço </InputLab>
          </ContainerLab>
            <Input
            onChangeText={onChange}
            OnBlur={OnBlur}
            value={value}
            placeholder='endereço do cliente'/>
            </ContainerInput>
          )} />

          <Controller
          control={control}
          name="bairro"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> Bairro </InputLab>
          </ContainerLab>
            <Input
            onChangeText={onChange}
            OnBlur={OnBlur}
            value={value}
            placeholder='bairro do cliente'/>
            </ContainerInput>
          )} />

          <Controller
          control={control}
          name="cidade"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> Cidade </InputLab>
          </ContainerLab>
            <Input
            onChangeText={onChange}
            OnBlur={OnBlur}
            value={value}
            placeholder='cidade do cliente'/>
            </ContainerInput>
          )} />

          <Controller
          control={control}
          name="uf"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> Estado </InputLab>
          </ContainerLab>
          <ContainerLab style={{ width: "100%", backgroundColor: "#dbdbdb", borderWidth: 2, borderRadius: 2, borderColor: "#677689" }}>
          <Picker
            selectedValue={selectedUF}
            onValueChange={(itemValue, itemIndex) =>
              setSelectedUF(itemValue)
            }>
            <Picker.Item label="Selecione por favor" value="" style={{color: "#677689"}} />
            <Picker.Item label="Tocantins" value="TO" style={{color: "#677689"}} />
            <Picker.Item label="São Paulo" value="SP" style={{color: "#677689"}} />
            <Picker.Item label="Sergipe" value="SE" style={{color: "#677689"}} />
            <Picker.Item label="Santa Catarina" value="SC" style={{color: "#677689"}} />
            <Picker.Item label="Roraima" value="RR" style={{color: "#677689"}} />
            <Picker.Item label="Rondônia" value="RO" style={{color: "#677689"}} />
            <Picker.Item label="Rio Grande do Sul" value="RS" style={{color: "#677689"}} />
            <Picker.Item label="Rio Grande do Norte" value="RN" style={{color: "#677689"}} />
            <Picker.Item label="Rio de Janeiro" value="RJ" style={{color: "#677689"}} />
            <Picker.Item label="Piauí" value="PI" style={{color: "#677689"}} />
            <Picker.Item label="Pernambuco" value="PE" style={{color: "#677689"}} />
            <Picker.Item label="Pará" value="PA" style={{color: "#677689"}} />
            <Picker.Item label="Paraíba" value="PB" style={{color: "#677689"}} />
            <Picker.Item label="Paraná" value="PR" style={{color: "#677689"}} />
            <Picker.Item label="Minas Gerais" value="MG" style={{color: "#677689"}} />
            <Picker.Item label="Mato Grosso do Sul" value="MS" style={{color: "#677689"}} />
            <Picker.Item label="Mato Grosso" value="MT" style={{color: "#677689"}} />
            <Picker.Item label="Maranhão" value="MA" style={{color: "#677689"}} />
            <Picker.Item label="Goiás" value="GO" style={{color: "#677689"}} />
            <Picker.Item label="Espírito Santo" value="ES" style={{color: "#677689"}} />
            <Picker.Item label="Distrito Federal" value="DF" style={{color: "#677689"}} />
            <Picker.Item label="Ceará" value="CE" style={{color: "#677689"}} />
            <Picker.Item label="Bahia" value="BA" style={{color: "#677689"}} />
            <Picker.Item label="Amazonas" value="AM" style={{color: "#677689"}} />
            <Picker.Item label="Amapá" value="AP" style={{color: "#677689"}} />
            <Picker.Item label="Alagoas" value="AL" style={{color: "#677689"}} />
            <Picker.Item label="Acre" value="AC" style={{color: "#677689"}} />
          </Picker>
          </ContainerLab>
            </ContainerInput>
          )} />

          <Controller
          control={control}
          name="cep"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> CEP </InputLab>
          </ContainerLab>
            <Input
            onChangeText={onChange}
            OnBlur={OnBlur}
            value={value}
            placeholder='cep do cliente'/>
            </ContainerInput>
          )} />

          <Controller
          control={control}
          name="complemento"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> Complemento </InputLab>
          </ContainerLab>
            <Input
            onChangeText={onChange}
            OnBlur={OnBlur}
            value={value}
            placeholder='complemento do cliente'/>
            </ContainerInput>
          )} />

          <Controller
          control={control}
          name="telefone1"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> Telefone(1) </InputLab>
          </ContainerLab>
            <Input
            onChangeText={onChange}
            OnBlur={OnBlur}
            value={value}
            placeholder='telefone(1) do cliente'/>
            </ContainerInput>
          )} />
		  
          <Controller
          control={control}
          name="telefone2"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> Telefone(2) </InputLab>
          </ContainerLab>
            <Input
            onChangeText={onChange}
            OnBlur={OnBlur}
            value={value}
            placeholder='telefone(2) do cliente'/>
            </ContainerInput>
          )} />

          <Controller
          control={control}
          name="telefone3"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> Telefone(3) </InputLab>
          </ContainerLab>
            <Input
            onChangeText={onChange}
            OnBlur={OnBlur}
            value={value}
            placeholder='telefone(3) do cliente'/>
            </ContainerInput>
          )} />

          <Controller
          control={control}
          name="celular"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> Celular </InputLab>
          </ContainerLab>
            <Input
            onChangeText={onChange}
            OnBlur={OnBlur}
            value={value}
            placeholder='celular do cliente'/>
            </ContainerInput>
          )} />

          <Controller
          control={control}
          name="email"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> Email </InputLab>
          </ContainerLab>
            <Input
            onChangeText={onChange}
            OnBlur={OnBlur}
            value={value}
            placeholder='email do cliente'/>
            </ContainerInput>
          )} />

          <Controller
          control={control}
          name="rg"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> RG </InputLab>
          </ContainerLab>
            <Input
            onChangeText={onChange}
            OnBlur={OnBlur}
            value={value}
            placeholder='rg do cliente'/>
            </ContainerInput>
          )} />

          <Controller
          control={control}
          name="cpf"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> CPF </InputLab>
          </ContainerLab>
            <Input
            onChangeText={onChange}
            OnBlur={OnBlur}
            value={value}
            placeholder='cpf do cliente'/>
            </ContainerInput>
          )} />

        <Controller
          control={control}
          name="tipo"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> Tipo </InputLab>
        </ContainerLab>
        <ContainerLab style={{ width: "100%", backgroundColor: "#dbdbdb", borderWidth: 2, borderRadius: 2, borderColor: "#677689" }}>
          <Picker 
            selectedValue={selectedTipo}
            onValueChange={(itemValue, itemIndex) =>
              setSelectedTipo(itemValue)
            }>
            <Picker.Item label="Selecione por favor" value="" style={{color: "#677689"}} />
            <Picker.Item label="Pessoa Física" value="1" style={{color: "#677689"}} />
            <Picker.Item label="Pessoa Jurídica" value="2" style={{color: "#677689"}} />
          </Picker>
          </ContainerLab>
        </ContainerInput>
          )} />

          <Controller
          control={control}
          name="obs1"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> OBS(1) </InputLab>
          </ContainerLab>
            <Input
            onChangeText={onChange}
            OnBlur={OnBlur}
            value={value}
            placeholder='obs(1) do cliente'/>
            </ContainerInput>
          )} />

          <Controller
          control={control}
          name="obs2"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> OBS(2) </InputLab>
          </ContainerLab>
            <Input
            onChangeText={onChange}
            OnBlur={OnBlur}
            value={value}
            placeholder='obs(2) do cliente'/>
            </ContainerInput>
          )} />

          <Controller
          control={control}
          name="obs3"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> OBS(3) </InputLab>
          </ContainerLab>
            <Input
            onChangeText={onChange}
            OnBlur={OnBlur}
            value={value}
            placeholder='obs(3) do cliente'/>
            </ContainerInput>
          )} />

          <Controller
          control={control}
          name="obs4"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> OBS(4) </InputLab>
          </ContainerLab>
            <Input
            onChangeText={onChange}
            OnBlur={OnBlur}
            value={value}
            placeholder='obs(4) do cliente'/>
            </ContainerInput>
          )} />

          <Controller
          control={control}
          name="obs5"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> OBS(5) </InputLab>
          </ContainerLab>
            <Input
            onChangeText={onChange}
            OnBlur={OnBlur}
            value={value}
            placeholder='obs(5) do cliente'/>
            </ContainerInput>
          )} />

          <Controller
          control={control}
          name="obs6"
          render={({ field: { onChange, OnBlur, value} }) => (
          <ContainerInput>
          <ContainerLab>
          <InputLab> OBS(6) </InputLab>
          </ContainerLab>
            <Input
            onChangeText={onChange}
            OnBlur={OnBlur}
            value={value}
            placeholder='obs(6) do cliente'/>
            </ContainerInput>
          )} />        

          </Form>

            <Save onPress={handleSubmit(handleEdit)}>
            <SaveLab> Salvar </SaveLab>
          </Save>
                      
        </Container>       
        
    )
  }

  export default CreateCliente;
