import { Alert, Modal, View,  FlatList, TouchableWithoutFeedback, ActivityIndicator} from 'react-native';
import React, { useCallback, useEffect, useState } from "react";
import {Container,  ContainerModal,  ContainerControlCliente, ContainerLabCliente, ContainerSelectCliente, 
Items, DataContent, ContainerControl, ContainerLab, ContainerInput, ContainerSelect, InputLab, EstoqueInputLab, 
Input, ContainerRadio, RadioLab, RadioCont, ContainerDisplay, DisplayCont, DisplayLab, DisplayLabDesc,
ValueLab, Icon, Icons, Save, SaveLab, LabSelect, SetSelect, Back, BackLab, DataView, Selected, DataEstoque, EstoqueLab, ErrorMensage } from "./styles/atendimento";
import { Content, BackContainer, BackContent, TopContainer, SearchInput, 
  ContainerSearch, Filter, DataLab, DataReplyLab, DataLimiter, IconsContent, IconContent, Separator} from "../cliente/styles/index";
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome';
import {  faPlus, faEquals, faCaretDown, faCircleCheck, faCircle, faArrowUpAZ, faMagnifyingGlass, } from "@fortawesome/free-solid-svg-icons";
import { useForm, Controller } from "react-hook-form";
import { RadioButton } from 'react-native-paper';
import {Picker} from '@react-native-picker/picker';
import { useQuery, QueryClient, useInfiniteQuery, QueryClientProvider } from "react-query";

const queryClient = new QueryClient()

function Atendimento() {
  return (
    <QueryClientProvider client={queryClient}>
      <Conteudo/>
    </QueryClientProvider>
  )
}

async function getClientes([pageParam, searchParam]) {
  const response = await fetch(
    `http://192.168.18.35/SmartMobile_Api/public/api/C000007?page=${pageParam}`
  );

   return response.json(); 

}

async function getCliente(clienteId) {
  const response = await fetch(
    `http://192.168.18.35/SmartMobile_Api/public/api/C000007/${clienteId}`
  );
  return response.json();
}

async function getProdutos(pageProd) {
  const response = await fetch(
    `http://192.168.18.35/SmartMobile_Api/public/api/C000025/?page=${pageProd}`
  );
  return response.json();
}

async function getProduto(produtoId) {
  const response = await fetch(
    `http://192.168.18.35/SmartMobile_Api/public/api/C000025/${produtoId}`
  );
  return response.json();
}

async function getSetores(produtoId) {
  const response = await fetch(
    `http://192.168.18.35/SmartMobile_Api/public/api/C000133/${produtoId}`
  );
  return response.json();
}

async function getSetor(setorId) {
  const response = await fetch(
    `http://192.168.18.35/SmartMobile_Api/public/api/C000133/setor/${setorId}`
  );
  return response.json();
}

async function getEstoque([ produtoId, setorId ]) {
  const response = await fetch(
    `http://192.168.18.35/SmartMobile_Api/public/api/C000100/${produtoId}/${setorId}`
  );
  return response.json();
}

   function Conteudo() {

    //Form
    const [selectedCliente, setSelectedCliente] = useState(1);
    const [selectedProd, setSelectedProd] = useState(0);
    const [selectedSetor, setSelectedSetor] = useState(0);
    const [tipoddesconto, setTipoddesconto] = useState('');
    const [quntd, setQuntd] = useState(1);
    const [prcntg, setPrcntg] = useState(0);
    const [reais, setReais] = useState(0);

    //Modal
    const [clientModal, setClientModal] = useState(false);
    const [prodModal, setProdModal] = useState(false);
    const [setorModal, setSetorModal] = useState(false);

    // Search
    const [searchClient, setSearchClient] = useState('');
    const [searchProd, setSearchProd] = useState('');
    const [searchSetores, setSearchSetores] = useState('');

    
    const [pageClient, setPageClient] = useState(1);
    const [pageProd, setPageProd] = useState(1);


    const clientes = useQuery( 
      ["clientes", pageClient, searchClient], () => getClientes([pageClient, searchClient]), {
        staleTime: 2000, cacheTime: 10
      },
    );

    const cliente = useQuery( 
      ["cliente", selectedCliente], () => getCliente(selectedCliente), {
        staleTime: 2000,
      },
    );

    const produtos = useQuery( 
      ["produtos", pageProd], () => getProdutos(pageProd), {
        staleTime: 2000,
      },
    );

    const produto = useQuery( 
      ["produto", selectedProd], () => getProduto(selectedProd), {
        staleTime: 2000,
      },
    );

    const setores = useQuery( 
      ["setores", selectedProd], 
      () => getSetores(selectedProd), {
        staleTime: 2000,
      },
    );

    useEffect(() => {
      if(setores.data == undefined) { }
      else {  if(setores.data.length == 1){ setSelectedSetor((setores.data.map((item) => item.CODIGO)).toString())
  
      } else if(setores.data.length == 0) { setSelectedSetor(0) }   }

    });

    const setor = useQuery( 
      ["setor", selectedSetor], () => getSetor(selectedSetor), {
        staleTime: 2000,
      },
      
    );

    const estoque = useQuery( 
      ["estoque", selectedProd, selectedSetor], () => getEstoque([ selectedProd, selectedSetor ]), {
        staleTime: 2000,
      },
    );

    const quantidade = produto.data === undefined? 0 : quntd === ''? parseFloat(produto.data.PRECOVENDA?.replace(",", ".")) * 1 : parseFloat(produto.data.PRECOVENDA?.replace(",", ".")) * parseFloat(quntd)
    const desconto = tipoddesconto === 'reais'? reais === ''? 0 : reais : tipoddesconto === 'prcntg'? prcntg === ''? 0 : (quantidade * parseFloat(prcntg) / 100) : 0
    const valorTotal = ((parseFloat(quantidade) - parseFloat(desconto)).toFixed(2)).replace(".", ",")

    const rxp = reais === ''? null : '- ' + (100 * parseFloat(reais) / quantidade).toFixed(2) + '%'
    const pxr = prcntg === ''? null : '- ' + (quantidade * parseFloat(prcntg) / 100).toFixed(2) + 'R$'

    const { control, handleSubmit, formState: { errors } } = useForm({ })


    if (clientes.isLoading) {
      return (
        <Container>
        <ContainerControlCliente>
        <LabSelect> Carregando... </LabSelect>
        </ContainerControlCliente>
        </Container>
      )
    }
    if (clientes.isError) {
      return (
        <Container>
        <ContainerControlCliente>
        <LabSelect> Erro </LabSelect>
        </ContainerControlCliente>
        </Container>
      )

    }
    if (clientes.isSuccess) {
      
      return(
        <Container>
              
        <ContainerControlCliente>
        <ContainerLabCliente>
        <InputLab> Cliente </InputLab>
        </ContainerLabCliente>         
      <ContainerSelectCliente onPress={() => setClientModal(true)}>
        
      {cliente.data == undefined?
      <LabSelect> Selecione por favor </LabSelect> : 
      <LabSelect> {cliente.data.NOME} {cliente.data.APELIDO} </LabSelect> } 
      
      <SetSelect>
      <FontAwesomeIcon icon={faCaretDown}  size={ 15 } color={ '#677689' }/>
      </SetSelect>
        </ContainerSelectCliente>
        </ContainerControlCliente>

      <Modal         
      visible={clientModal}
      transparent={true}
      animationType="fade"
      onRequestClose={() => setClientModal(false)}>

      <ContainerModal>

      <TopContainer>
      <ContainerSearch>
      <FontAwesomeIcon icon={faMagnifyingGlass} size={ 25 } color={ '#677689' }/>
      </ContainerSearch>                       
      <SearchInput
      placeholder='Buscar Cliente'
      name={searchClient}
      onChangeText={(t) => setSearchClient(t)}/>                          
      </TopContainer>
      
      <DataView>

    <FlatList
        data={clientes.data}    
        keyExtractor={(item) => item.CODIGO}    
        ListFooterComponent={ <ActivityIndicator size={'large'} color={'#677689'} /> }
        renderItem={({ item }) => {
          return <View style={{ margin: 10 }}>
          <DataLab key={item.CODIGO}> Codigo: <DataReplyLab> {item.CODIGO} </DataReplyLab> </DataLab>
          <DataLab> Cliente: <DataReplyLab> {item.NOME} {item.APELIDO} </DataReplyLab> </DataLab>
          <DataLab> CPF: <DataReplyLab> {item.CPF} </DataReplyLab> </DataLab>
        
        {item.CODIGO == selectedCliente? <Selected>
        <FontAwesomeIcon icon={faCircleCheck}  size={ 35 } color={ '#8395ab' }/>       
        </Selected> : <Selected onPress={() => setSelectedCliente(item.CODIGO)}>
        <FontAwesomeIcon icon={faCircle}  size={ 35 } color={ '#b9b7b7' }/>       
        </Selected> }
        </View>;
        }}/>

    </DataView>

    </ContainerModal>       
    <Back onPress={() => setClientModal(false)}>
    <BackLab> Voltar </BackLab>
    </Back>

    </Modal>     

      <Icons>
      <Icon>
      <FontAwesomeIcon icon={faPlus}  size={ 25 } color={ '#dbdbdb' }/>       
      </Icon>

      <Icon>
      <FontAwesomeIcon icon={faEquals}  size={ 25 } color={ '#dbdbdb' }/>
      </Icon>
      </Icons>

      <DataContent>

      <Controller
      control={control}
      name="produto"
      render={({ field: { onChange, OnBlur, value} }) => (   
        <ContainerControl>
        <ContainerLab>
        <InputLab> Produtos </InputLab>
        </ContainerLab>  
      <ContainerSelect onPress={() => setProdModal(true)}>

      {produto.data == undefined?
      <LabSelect> Selecione por favor </LabSelect> : 
      <LabSelect> {produto.data.PRODUTO} </LabSelect> }

      <SetSelect>
      <FontAwesomeIcon icon={faCaretDown}  size={ 15 } color={ '#677689' }/>
      </SetSelect>
      
        </ContainerSelect>
        </ContainerControl>
    )}/>

      <Modal         
      visible={prodModal}
      transparent={true}
      animationType="fade"
      onRequestClose={() => setProdModal(false)}>

      <ContainerModal>

      <TopContainer>
      <ContainerSearch>
      <FontAwesomeIcon icon={faMagnifyingGlass} size={ 25 } color={ '#677689' }/>
      </ContainerSearch>                       
      <SearchInput
      placeholder='Buscar Produto'
      name={searchProd}
      onChangeText={(t) => setSearchProd(t)}/>                          
      </TopContainer>
     
    <DataView>

    <View key={0} style={{ margin: 10 }}  onPress={() => { setSelectedSetor(''); setSelectedProd(0) }}>

    <DataLab> Selecione por favor </DataLab>

    {selectedProd == 0? 
    <Selected>
    <FontAwesomeIcon icon={faCircleCheck}  size={ 35 } color={ '#8395ab' }/>       
    </Selected> :
    <Selected onPress={() => setSelectedProd(0)}>
    <FontAwesomeIcon icon={faCircle}  size={ 35 } color={ '#b9b7b7' }/>       
    </Selected>
    }

    </View>

    <FlatList
        data={produtos.data}            
        keyExtractor={item => item.CODIGO}
        renderItem={({ item }) => {
          return <View key={item.CODIGO} style={{ margin: 10 }}>

          <DataLab> Codigo: <DataReplyLab> {item.CODIGO} </DataReplyLab> </DataLab>
          <DataLab> Produto: <DataReplyLab> {item.PRODUTO} </DataReplyLab> </DataLab>
          <DataLab> Pre√ßo: <DataReplyLab> {item.PRECOVENDA} </DataReplyLab> </DataLab>
          <DataLab> Codbarra: <DataReplyLab> {item.CODBARRA} </DataReplyLab> </DataLab>

          {item.CODIGO == selectedProd? 
          <Selected>
          <FontAwesomeIcon icon={faCircleCheck}  size={ 35 } color={ '#8395ab' }/>       
          </Selected> :
          <Selected onPress={() => setSelectedProd(item.CODIGO)}>
          <FontAwesomeIcon icon={faCircle}  size={ 35 } color={ '#b9b7b7' }/>       
          </Selected>
          }

          </View>;
        }}/>

    </DataView>
    </ContainerModal>
    <Back onPress={() => setProdModal(false)}>
    <BackLab> Voltar </BackLab>
    </Back>

    </Modal>

    {setores.data == undefined? null : setores.data.length < 2? null :

      <Controller
      control={control}
      name="setor"
      render={({ field: { onChange, OnBlur, value} }) => ( 
        <ContainerControl>
        <ContainerLab>
        <InputLab> Setor </InputLab>
        </ContainerLab>     
      <ContainerSelect onPress={() => setSetorModal(true)}>

      {setor.data == undefined || selectedSetor == 0?
      <LabSelect> Selecione por favor </LabSelect> : 
      <LabSelect> {setor.data.SETOR} </LabSelect> }

      <SetSelect>
      <FontAwesomeIcon icon={faCaretDown}  size={ 15 } color={ '#677689' }/>
      </SetSelect>

        </ContainerSelect>
        </ContainerControl>
    )}/>
    
  }  

      <Modal         
      visible={setorModal}
      transparent={true}
      animationType="fade"
      onRequestClose={() => setSetorModal(false)}>

      <ContainerModal>

      <TopContainer>
      <ContainerSearch>
      <FontAwesomeIcon icon={faMagnifyingGlass} size={ 25 } color={ '#677689' }/>
      </ContainerSearch>                       
      <SearchInput
      placeholder='Buscar Setor'
      name={searchSetores}
      onChangeText={(t) => setSearchSetores(t)}/>                          
      </TopContainer>
      
    <DataView>

    <DataEstoque>
    {estoque.data == undefined || selectedSetor == 0?
      <EstoqueLab> Selecione um setor para visualizar o estoque </EstoqueLab> : 
      <EstoqueLab> Estoque atual: {estoque.data.ESTOQUE_ATUAL} </EstoqueLab> }
    </DataEstoque>

    <View key={0} style={{ margin: 10 }}>

    <DataLab> Selecione por favor </DataLab>

    {selectedSetor == 0? 
    <Selected>
    <FontAwesomeIcon icon={faCircleCheck}  size={ 35 } color={ '#8395ab' }/>       
    </Selected> :
    <Selected onPress={() => setSelectedSetor(0)}>
    <FontAwesomeIcon icon={faCircle}  size={ 35 } color={ '#b9b7b7' }/>       
    </Selected>
    }

    </View>

    

    <FlatList
        data={setores.data}            
        keyExtractor={item => item.CODIGO}
        renderItem={({ item }) => { 
          return <View key={item.CODIGO} style={{ margin: 10 }}  onPress={() => setSelectedSetor(item.CODIGO)}>

          <DataLab> Codigo: <DataReplyLab> {item.CODIGO} </DataReplyLab> </DataLab>
          <DataLab> Setor: <DataReplyLab> {item.SETOR} </DataReplyLab> </DataLab>
                  
          {item.CODIGO == selectedSetor?
          <Selected>
          <FontAwesomeIcon icon={faCircleCheck}  size={ 35 } color={ '#8395ab' }/>       
          </Selected> :
          <Selected onPress={() => setSelectedSetor(item.CODIGO)}>
          <FontAwesomeIcon icon={faCircle}  size={ 35 } color={ '#b9b7b7' }/>       
          </Selected>
          }

          </View>;
        }}/>


    </DataView>
    </ContainerModal>
    <Back onPress={() => setSetorModal(false)}>
    <BackLab> Voltar </BackLab>
    </Back>

    </Modal>     
   

      <Controller
      control={control}
      name="quntd"
      render={({ field: { onChange, OnBlur, value} }) => (
        <ContainerControl>
        <ContainerLab>
        <InputLab> Quantidade </InputLab>
        { estoque.data == undefined? null  : <EstoqueInputLab> (1 - {estoque.data.ESTOQUE_ATUAL}) </EstoqueInputLab>  }
        </ContainerLab>      
      <ContainerInput>
      <Input
      name={quntd}
      onChangeText={(t) => setQuntd(t)}
      keyboardType='numeric'
      OnBlur={OnBlur}
      value={value}
      placeholder='Informe quantidade'/>
      </ContainerInput>
      </ContainerControl>
    )}/>

      <Controller
      control={control}
      name="desc"
      render={({ field: { onChange, OnBlur, value} }) => (
        <ContainerControl>
        <ContainerLab>
        <InputLab> Desconto </InputLab>
        </ContainerLab>
      <ContainerRadio>
      <RadioCont>
      <RadioButton
      color='#677689'
      value="prcntg"
      status={ tipoddesconto === 'prcntg' ? 'checked' : 'unchecked' }
      onPress={() => setTipoddesconto('prcntg') } />
      <RadioLab> % </RadioLab>
      </RadioCont>
      <RadioCont>
      <RadioButton
      color='#677689'
      value="reais"
      status={ tipoddesconto === 'reais' ? 'checked' : 'unchecked' }
      onPress={() => setTipoddesconto('reais') } />
      <RadioLab> R$ </RadioLab>
      </RadioCont>
      </ContainerRadio>
      </ContainerControl>
    )}/>

    {tipoddesconto == 'prcntg'? 

      <Controller
      control={control}
      name="prcntg"
      render={({ field: { onChange, OnBlur, value} }) => (
        <ContainerControl>
        <ContainerLab>
        <InputLab> % </InputLab>
        </ContainerLab>      
      <ContainerInput>
      <Input
      keyboardType='numeric'
      name={prcntg}
      onChangeText={(t) => setPrcntg(t)}
      OnBlur={OnBlur}
      value={value}
      placeholder='desconto em %'>{prcntg}</Input>
      </ContainerInput>
      </ContainerControl>
    )}/> : null }

{tipoddesconto == 'reais'? 

      <Controller
      control={control}
      name="reais"
      render={({ field: { onChange, OnBlur, value} }) => (
        <ContainerControl>
        <ContainerLab>
        <InputLab> R$ </InputLab>
        </ContainerLab>      
      <ContainerInput>
      <Input
      keyboardType='numeric'
      name={reais}
      onChangeText={(t) => setReais(t)}
      OnBlur={OnBlur}
      value={value}
      placeholder='desconto em R$'>{reais}</Input>
      </ContainerInput>
      </ContainerControl>
    )}/> : null }

      </DataContent>

      <ContainerDisplay>
      <DisplayCont>
       <DisplayLab> Pre√ßo(R$): </DisplayLab> 
    { produto.data === undefined? <ValueLab> 0,00 </ValueLab> :
    <ValueLab> {produto.data.PRECOVENDA} </ValueLab>  }        
      </DisplayCont>
      <DisplayCont>
      { tipoddesconto === 'reais' ? <DisplayLab> Total(R$): <DisplayLabDesc> {rxp} </DisplayLabDesc> </DisplayLab> :
      tipoddesconto === 'prcntg' ?  <DisplayLab> Total(R$): <DisplayLabDesc> {pxr} </DisplayLabDesc>  </DisplayLab> :
      <DisplayLab> Total(R$): </DisplayLab>  }
       <ValueLab> {valorTotal} </ValueLab> 
      </DisplayCont>
      </ContainerDisplay>

    
     <Save>
     <SaveLab> Proximo </SaveLab>
     </Save>

    </Container>
     ) 
    
  }
    
  };

  export default Atendimento;
