import React, { useEffect, useState } from "react";
import { View, Button, FlatList, Alert, Modal, RefreshControl } from 'react-native';
import { Container, Content, BackContainer, BackContent, TopContainer, SearchInput, 
  ContainerSearch, Filter, BarCode, DataContent, DataLab, DataReplyLab, DataLimiter, IconsContent, IconContent } from "./styles";
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome';
import { faRightFromBracket, faHouse, faArrowUpAZ, faBarcode, faMagnifyingGlass, faEye, faPen } from "@fortawesome/free-solid-svg-icons";
import Scanner from "./scanner";

const Preco = ({ navigation }) => {
  
  const [refreshing, setRefreshing] = React.useState(false);
  const [dados, setDados] = useState([]);
  const [searchText, setSearchText] = useState('');

  useEffect(() => {  
      setRefreshing(true);
      getData();
  }, [searchText]); 

  const getData = () => {
    fetch('http://192.168.18.35/SmartMobile_Api/public/api/C000025', {
      method: 'GET',
      headers: {
        'Accept': 'application/json'
      }
    })
       .then(response => response.json())
       .then(data => {
        
        if(searchText === '') {
          setRefreshing(true);
          setDados(data);
          setRefreshing(false);
         } else {
          setRefreshing(true);
           setDados(
             data.filter(
               (fil) => 
                fil.CODBARRA.indexOf(searchText) > -1 || fil.PRODUTO.toLowerCase().indexOf(searchText.toLowerCase()) > -1 
             )
           );
           setRefreshing(false);
             }
       })
    };

      const handleOrderClick = () => {
        setRefreshing(true);
        let newList = [...dados];
        newList.sort((a, b) => (a.PRODUTO > b.PRODUTO ? 1 : b.PRODUTO > a.PRODUTO ? -1 : 0));   
        setDados(newList);
        setRefreshing(false);
      };
      
      const [modalVisible, setModalVisible] = React.useState(false);
      const [data, setData] = React.useState("");
    
      const onCodeScanned = (type, data) => {       
        setModalVisible(false);
        setSearchText(data);
        setData(data);
            
      };

      const onRefresh = (data) => {
        setRefreshing(true);
        setDados(data);
        getData();

      };

      function renderItem({ item }){
        return <DataContent>
               
            <DataLimiter>
            <DataLab> Codigo: <DataReplyLab> {item.CODIGO} </DataReplyLab> </DataLab>
            <DataLab> Produto: <DataReplyLab> {item.PRODUTO} </DataReplyLab> </DataLab>
            <DataLab> Preço: <DataReplyLab> {item.PRECOVENDA} </DataReplyLab> </DataLab>
            </DataLimiter>

            <IconsContent>
            <IconContent onPress={() => navigation.navigate('ShowPreco', {
              cod: item.CODIGO,
              produto: item.PRODUTO,
              codgrupo: item.CODGRUPO,
              codsubgrupo:item.CODSUBGRUPO,
              codfornecedor: item.CODFORNECEDOR,
              codmarca: item.CODMARCA,
              data_ultimacompra: item.DATA_ULTIMACOMPRA,
              precocusto: item.PRECOCUSTO,
              precovenda: item.PRECOVENDA,
              data_ultimavenda: item.DATA_ULTIMAVENDA,
              aplicacao: item.APLICACAO,
              localicazao: item.LOCALICAZAO,
              foto: item.FOTO,
              cst: item.CST,
              classificacao_fiscal: item.CLASSIFICACAO_FISCAL,
              aliquota: item.ALIQUOTA,
              situacao: item.SITUACAO,
              precovenda1: item.PRECOVENDA1,
              precovenda2: item.PRECOVENDA2,
              referencia_fornecedor: item.REFERENCIA_FORNECEDOR,
              csosn: item.CSOSN,
              cest: item.CEST,
              ind_cfop_nfce: item.IND_CFOP_NFCE,
              codbarra: item.CODBARRA,
              unidade: item.UNIDADE,
              ecommerce: item.ECOMMERCE, })}>
            <FontAwesomeIcon icon={faEye} size={ 21 } color={ '#dbdbdb' } />  
            </IconContent>
            <IconContent onPress={() => navigation.navigate('EditPreco', {
              cod: item.CODIGO,
              produto: item.PRODUTO,
              codgrupo: item.CODGRUPO,
              codsubgrupo:item.CODSUBGRUPO,
              codfornecedor: item.CODFORNECEDOR,
              codmarca: item.CODMARCA,
              data_ultimacompra: item.DATA_ULTIMACOMPRA,
              precocusto: item.PRECOCUSTO,
              precovenda: item.PRECOVENDA,
              data_ultimavenda: item.DATA_ULTIMAVENDA,
              aplicacao: item.APLICACAO,
              localicazao: item.LOCALICAZAO,
              foto: item.FOTO,
              cst: item.CST,
              classificacao_fiscal: item.CLASSIFICACAO_FISCAL,
              aliquota: item.ALIQUOTA,
              situacao: item.SITUACAO,
              precovenda1: item.PRECOVENDA1,
              precovenda2: item.PRECOVENDA2,
              referencia_fornecedor: item.REFERENCIA_FORNECEDOR,
              csosn: item.CSOSN,
              cest: item.CEST,
              ind_cfop_nfce: item.IND_CFOP_NFCE,
              codbarra: item.CODBARRA,
              unidade: item.UNIDADE,
              ecommerce: item.ECOMMERCE, })}>
            <FontAwesomeIcon icon={faPen} size={ 15 } color={ '#dbdbdb' } />  
            </IconContent>
            </IconsContent>

            </DataContent>;
      }

    return (
        <Container>
          <Modal         
          visible={modalVisible}
          transparent={true}
          animationType="fade"
          onRequestClose={() => setModalVisible(false)}>

          <View style={{ flex: 1,
            alignItems: "center",
            justifyContent: "space-around",
            backgroundColor: "lightgrey", }}>
          <Scanner onCodeScanned={onCodeScanned} />
          <Button title="Cancelar" color="#677689" onPress={() => setModalVisible(false)} />
          </View>
          </Modal>

          <TopContainer>
          <ContainerSearch>
          <FontAwesomeIcon icon={faMagnifyingGlass} size={ 25 } color={ '#677689' }/>
          </ContainerSearch>                       
          <SearchInput
          placeholder='Buscar Produto'
          name={searchText}
          onChangeText={(t) => setSearchText(t)}>{data}</SearchInput>
          <Filter onPress={handleOrderClick}>                               
          <FontAwesomeIcon icon={faArrowUpAZ}  size={ 25 } color={ '#677689' }/>
          </Filter>
          <BarCode onPress={() => setModalVisible(true)}>                               
          <FontAwesomeIcon icon={faBarcode}  size={ 25 } color={ '#dbdbdb' }/>
          </BarCode>                             
          </TopContainer>
            
          <Content Style={{alignItems: 'center' }}>

            <FlatList
            data={dados}            
            keyExtractor={item => item.CODIGO}
            refreshControl={ <RefreshControl refreshing={refreshing}  onRefresh={onRefresh} /> }
            renderItem={renderItem}/>
          
          </Content> 

          <BackContainer>
            <BackContent onPress={() => navigation.navigate('Home')}>             
            <FontAwesomeIcon icon={faHouse} size={ 52 } color={ '#677689' }/>
            </BackContent>
            <BackContent onPress={() => navigation.navigate('Login')}>             
            <FontAwesomeIcon icon={faRightFromBracket} size={ 52 } color={ '#677689' }/>
            </BackContent>
          </BackContainer>
          
        </Container>
    )
  }

  export default Preco;
