import { Alert } from 'react-native';
import { Container, Form, ContainerInput, Input, ContainerLab, InputLab, Save, SaveLab, ErrorMensage } from "./styles/form";
import { useForm, Controller } from "react-hook-form";
import { yupResolver } from '@hookform/resolvers/yup'
import * as yup from 'yup'

const EditPreco = ({route, navigation}) => {

      const schema = yup.object({
        produto: yup.string().required("Por favor preencha esse campo"),
        codbarra: yup.string().required("Por favor preencha esse campo")
      })

      const { control, handleSubmit, formState: { errors } } = useForm({
        
        defaultValues: {     
          produto: route.params.produto,
          codgrupo: route.params.codgrupo,
          codsubgrupo:route.params.codsubgrupo,
          codfornecedor: route.params.codfornecedor,
          codmarca: route.params.codmarca,
          precocusto: route.params.precocusto,
          precovenda: route.params.precovenda,
          aplicacao: route.params.aplicacao,
          localicazao: route.params.localicazao,
          foto: route.params.foto,
          cst: route.params.cst,
          classificacao_fiscal: route.params.classificacao_fiscal,
          aliquota: route.params.aliquota,
          referencia_fornecedor: route.params.referencia_fornecedor,
          csosn: route.params.csosn,
          cest: route.params.cest,
          ind_cfop_nfce: route.params.ind_cfop_nfce,
          codbarra: route.params.codbarra,
          unidade: route.params.unidade
        },
        
          resolver: yupResolver(schema),
    
      })

      let handleEdit = async (data) => {

          fetch(`http://192.168.18.35/SmartMobile_Api/public/api/C000025/${route.params.cod}`, {
          method: 'PUT',
          body: JSON.stringify({
            PRODUTO: data.produto,
            CODGRUPO: data.codgrupo,
            CODSUBGRUPO:data.codsubgrupo,
            CODFORNECEDOR: data.codfornecedor,
            CODMARCA: data.codmarca,
            PRECOCUSTO: data.precocusto,
            PRECOVENDA: data.precovenda,
            APLICACAO: data.aplicacao,
            LOCALICAZAO: data.localicazao,
            FOTO: data.foto,
            CST: data.cst,
            CLASSIFICACAO_FISCAL: data.classificacao_fiscal,
            ALIQUOTA: data.aliquota,
            REFERENCIA_FORNECEDOR: data.referencia_fornecedor,
            CSOSN: data.csosn,
            CEST: data.cest,
            IND_CFOP_NFCE: data.ind_cfop_nfce,
            CODBARRA: data.codbarra,
            UNIDADE: data.unidade
          }),
          headers: {
            'Content-type': 'application/json; charset=UTF-8',
          },
        })
          .then((response) => response.json())
          .then((json) => console.log(json));
          Alert.alert(
            "Atualização bem sucedida",
            'O produto foi atualizado com sucesso.',
            [ 
              { text: "Voltar", onPress: () => navigation.navigate('Cliente') }
            ]
          );
      }

    return (
      <Container>
      <Form contentContainerStyle={{ alignItems: 'center' }}>

      <Controller
      control={control}
      name="produto"
      render={({ field: { onChange, OnBlur, value} }) => (
      <ContainerInput>
      <ContainerLab>
      <InputLab> Produto </InputLab>
      </ContainerLab>
        <Input
        onChangeText={onChange}
        OnBlur={OnBlur}
        value={value}
        placeholder='nome do produto'/>
        </ContainerInput>
      )} />

      {errors.produto && <ErrorMensage> {errors.produto?.message} </ErrorMensage>}

                <Controller
      control={control}
      name="codgrupo"
      render={({ field: { onChange, OnBlur, value} }) => (
      <ContainerInput>
      <ContainerLab>
      <InputLab> Código do Grupo </InputLab>
      </ContainerLab>
        <Input
        onChangeText={onChange}
        OnBlur={OnBlur}
        value={value}
        placeholder='código do grupo do produto'/>
        </ContainerInput>
      )} />

      <Controller
      control={control}
      name="codsubgrupo"
      render={({ field: { onChange, OnBlur, value} }) => (
      <ContainerInput>
      <ContainerLab>
      <InputLab> Código do Subgrupo </InputLab>
      </ContainerLab>
        <Input
        onChangeText={onChange}
        OnBlur={OnBlur}
        value={value}
        placeholder='código do subgrupo do produto'/>
        </ContainerInput>
      )} />

      <Controller
      control={control}
      name="codfornecedor"
      render={({ field: { onChange, OnBlur, value} }) => (
      <ContainerInput>
      <ContainerLab>
      <InputLab> Código do Fornecedor </InputLab>
      </ContainerLab>
        <Input
        onChangeText={onChange}
        OnBlur={OnBlur}
        value={value}
        placeholder='código do fornecedor do produto'/>
        </ContainerInput>
      )} />

      <Controller
      control={control}
      name="codmarca"
      render={({ field: { onChange, OnBlur, value} }) => (
      <ContainerInput>
      <ContainerLab>
      <InputLab> Código da Marca </InputLab>
      </ContainerLab>
        <Input
        onChangeText={onChange}
        OnBlur={OnBlur}
        value={value}
        placeholder='código da do produto'/>
        </ContainerInput>
      )} />

      <Controller
      control={control}
      name="precocusto"
      render={({ field: { onChange, OnBlur, value} }) => (
      <ContainerInput>
      <ContainerLab>
      <InputLab> Preço de custo </InputLab>
      </ContainerLab>
        <Input
        onChangeText={onChange}
        OnBlur={OnBlur}
        value={value}
        placeholder='preço de custo do produto'/>
        </ContainerInput>
      )} />

      <Controller
      control={control}
      name="precovenda"
      render={({ field: { onChange, OnBlur, value} }) => (
        <ContainerInput>
        <ContainerLab>
        <InputLab> Preço de Venda </InputLab>
        </ContainerLab>
          <Input
          onChangeText={onChange}
          OnBlur={OnBlur}
          value={value}
          placeholder='preço de venda do produto'/>
          </ContainerInput>
      )} />

      <Controller
      control={control}
      name="aplicacao"
      render={({ field: { onChange, OnBlur, value} }) => (
      <ContainerInput>
      <ContainerLab>
      <InputLab> Aplicação </InputLab>
      </ContainerLab>
        <Input
        onChangeText={onChange}
        OnBlur={OnBlur}
        value={value}
        placeholder='aplicação do produto'/>
        </ContainerInput>
      )} />

      <Controller
      control={control}
      name="localicazao"
      render={({ field: { onChange, OnBlur, value} }) => (
      <ContainerInput>
      <ContainerLab>
      <InputLab> Localização </InputLab>
      </ContainerLab>
        <Input
        onChangeText={onChange}
        OnBlur={OnBlur}
        value={value}
        placeholder='localização do produto'/>
        </ContainerInput>
      )} />
 
      <Controller
