   Inputs, Input } from "./styles/login";

   function Login({ navigation }) {
  
  const [loaded] = useFonts({
    MajorMonoDisplay: require('./fonts/MajorMonoDisplay-Regular.ttf'),
  });
  
  if (!loaded) {
    return null;
  }
  
    return (     
        <Container>
          <StatusBar backgroundColor="#677689"/>
          <PurpleCircle>
          </PurpleCircle>  
