import { Alert, Modal, TouchableOpacity,  FlatList } from 'react-native';
import React, { useEffect, useState } from "react";
import {Container,  ContainerModal,  ContainerControlCliente, ContainerLabCliente, ContainerSelectCliente, 
Items, DataContent, ContainerControl, ContainerLab, ContainerInput, ContainerSelect, InputLab, EstoqueInputLab, 
Input, ContainerRadio, RadioLab, RadioCont, ContainerDisplay, DisplayCont, DisplayLab, DisplayLabDesc,
ValueLab, Icon, Icons, Save, SaveLab, LabSelect, SetSelect, Back, BackLab, DataView, DataEstoque, EstoqueLab, ErrorMensage } from "./styles/atendimento";
import { Content, BackContainer, BackContent, TopContainer, SearchInput, 
  ContainerSearch, Filter, DataLab, DataReplyLab, DataLimiter, IconsContent, IconContent, Separator, Selected} from "../cliente/styles/index";
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome';
import {  faPlus, faEquals, faCaretDown, faCircleCheck, faArrowUpAZ, faMagnifyingGlass, } from "@fortawesome/free-solid-svg-icons";
import { useForm, Controller } from "react-hook-form";
import { RadioButton } from 'react-native-paper';
import {Picker} from '@react-native-picker/picker';


   function Atendimento({ navigation }) {
    //MODAL
    const [clientModal, setClientModal] = React.useState(false);
    const [prodModal, setProdModal] = React.useState(false);
    const [setorModal, setSetorModal] = React.useState(false);
    const [editprcntg, setEditprcntg ] = React.useState(false); 
    const [editreais, setEditreais ] = React.useState(false);

    // API
    const [clientes, setClientes] = React.useState([]);
    const [cliente, setCliente] = React.useState([]);
    const [produtos, setProdutos] = React.useState([]);
    const [produto, setProduto] = React.useState(0);
    const [setores, setSetores] = React.useState([]);
    const [setor, setSetor] = React.useState([]);
    const [estoque, setEstoque] = React.useState([]);

    // FORM  
    const [selectedClient, setSelectedClient] = useState(1);
    const [selectedProd, setSelectedProd] = useState('');
    const [selectedSetor, setSelectedSetor] = useState('');
    const [tipoddesconto, setTipoddesconto] = React.useState('');
    const [quntd, setQuntd] = React.useState(1);
    const [prcntg, setPrcntg] = React.useState(0);
    const [reais, setReais] = React.useState(0);
    
    // SEARCH
    const [searchClient, setSearchClient] = useState('');
    const [searchProd, setSearchProd] = useState('');
    const [searchSetores, setSearchSetores] = useState('');

    useEffect(() => {

      
      
      // CLIENTES

      fetch('http://192.168.18.35/SmartMobile_Api/public/api/C000007', {
        method: 'GET',
        headers: {
          'Accept': 'application/json'
        }
      })
         .then(response => response.json())    
         .then(json => {
          if(searchClient === '') {
            setClientes(json);
           } else {
            setClientes(
              json.filter(
                 (fil) => 
                  fil.CPF.indexOf(searchClient) > -1 || fil.NOME.toLowerCase().indexOf(searchClient.toLowerCase()) > -1 
               )
             );
               }
         })

      // CLIENTE

      fetch(`http://192.168.18.35/SmartMobile_Api/public/api/C000007/${selectedClient}`, {
        method: 'GET',
        headers: {
          'Accept': 'application/json'
        }
      })
      .then(response => response.json())   
         .then(json => {
            setCliente(json);
         })

      //PRODUTOS

      fetch('http://192.168.18.35/SmartMobile_Api/public/api/C000025', {
        method: 'GET',
        headers: {
          'Accept': 'application/json'
        }
      })
         .then(response => response.json())
         .then((json) => {
          if(searchProd === '') {
            setProdutos(json);
           } else {
            setProdutos(
              json.filter(
                 (fil) => 
                  fil.CODBARRA.indexOf(searchProd) > -1 || fil.PRODUTO.toLowerCase().indexOf(searchProd.toLowerCase()) > -1 
               )
             );
               }
               })

      //PRODUTO

        fetch(`http://192.168.18.35/SmartMobile_Api/public/api/C000025/${selectedProd}`, {
        method: 'GET',
        headers: {
          'Accept': 'application/json'
        }
      })
      .then((response) => response.json())    
      .then((json) => setProduto(json));

    //SETORES

      fetch(`http://192.168.18.35/SmartMobile_Api/public/api/C000133/${selectedProd}`, {
        method: 'GET',
        headers: {
          'Accept': 'application/json'
        }
      })
      .then((response) => response.json())
      .then((json) =>  {

        if(selectedProd === '') {
          setSetores([]);
        }
        else if(searchSetores === '') {
          setSetores(json);
         } 
         else {
          setSetores(
            json.filter(
               (fil) => 
                fil.SETOR.toLowerCase().indexOf(searchSetores.toLowerCase()) > -1 
             )
           );
             }
            
             });
             
    //SETOR

    fetch(`http://192.168.18.35/SmartMobile_Api/public/api/C000133/setor/${selectedSetor}`, {
      method: 'GET',
      headers: {
        'Accept': 'application/json'
      }
    })
    .then((response) => response.json())
    .then((json) => {

      if(setores.length == 1){
  
        setSelectedSetor((setores.map((item) => item.CODIGO)).toString())
  
      } else {
        setSetor(json);
      }
    });

    console.log(setor)
     
     //ESTOQUE

     fetch(`http://192.168.18.35/SmartMobile_Api/public/api/C000100/${selectedProd}/${selectedSetor}`, {
      method: 'GET',
      headers: {
        'Accept': 'application/json'
      }
    })
    .then((response) => response.json())
    .then((json) => setEstoque(json));
                  
      }, [searchClient, selectedClient, searchProd, selectedProd, searchSetores, selectedSetor, tipoddesconto, prcntg, setor ]);
    
      const reaisRadio = () => {
        setTipoddesconto('reais');
        setEditreais(true); 
        setEditprcntg(false);
      }; 
      const prcntgRadio = () => {
        setTipoddesconto('prcntg');
        setEditreais(false); 
        setEditprcntg(true);
      };
      const quantidade = produto.PRECOVENDA === undefined? 0 : parseFloat(produto.PRECOVENDA?.replace(",", ".")) * parseFloat(quntd)
      const desconto = tipoddesconto === 'reais'? reais : tipoddesconto === 'prcntg'? (quantidade * parseFloat(prcntg) / 100) : 0
      const valorTotal = ((parseFloat(quantidade) - parseFloat(desconto)).toFixed(2)).replace(".", ",")

      const rxp =  (100 * parseFloat(reais) / quantidade).toFixed(2) 
      const pxr = (quantidade * parseFloat(prcntg) / 100).toFixed(2)

      const { control, handleSubmit, formState: { errors } } = useForm({ })
  
    return (     
        <Container>
              
            <ContainerControlCliente>
            <ContainerLabCliente>
            <InputLab> Cliente </InputLab>
            </ContainerLabCliente>         
          <ContainerSelectCliente onPress={() => setClientModal(true)}>
            
          {selectedClient == ''?
          <LabSelect> Selecione por favor </LabSelect> : 
          <LabSelect> {cliente.NOME} {cliente.APELIDO} </LabSelect> } 
          
          <SetSelect>
          <FontAwesomeIcon icon={faCaretDown}  size={ 15 } color={ '#677689' }/>
          </SetSelect>
            </ContainerSelectCliente>
            </ContainerControlCliente>

          <Modal         
          visible={clientModal}
          transparent={true}
          animationType="fade"
          onRequestClose={() => setClientModal(false)}>

          <ContainerModal>

          <TopContainer>
          <ContainerSearch>
          <FontAwesomeIcon icon={faMagnifyingGlass} size={ 25 } color={ '#677689' }/>
          </ContainerSearch>                       
          <SearchInput
          placeholder='Buscar Cliente'
          name={searchClient}
          onChangeText={(t) => setSearchClient(t)}/>                          
          </TopContainer>
          
          <DataView>

        <FlatList
            data={clientes}            
            keyExtractor={item => item.CODIGO}
            renderItem={({ item }) => {
              return <TouchableOpacity key={item.CODIGO} style={{ margin: 10 }}  onPress={() => setSelectedClient(item.CODIGO)}>
              <DataLab> Codigo: <DataReplyLab> {item.CODIGO} </DataReplyLab> </DataLab>
              <DataLab> Cliente: <DataReplyLab> {item.NOME} {item.APELIDO} </DataReplyLab> </DataLab>
              <DataLab> CPF: <DataReplyLab> {item.CPF} </DataReplyLab> </DataLab>

              {selectedClient == item.CODIGO? <Selected>
          <FontAwesomeIcon icon={faCircleCheck}  size={ 25 } color={ '#8395ab' }/>       
          </Selected> : null}
          
              </TouchableOpacity>;
            }}/>

        </DataView>

        </ContainerModal>       
        <Back onPress={() => setClientModal(false)}>
        <BackLab> Voltar </BackLab>
        </Back>

        </Modal>     

          <Icons>
          <Icon>
          <FontAwesomeIcon icon={faPlus}  size={ 25 } color={ '#dbdbdb' }/>       
          </Icon>

          <Icon>
          <FontAwesomeIcon icon={faEquals}  size={ 25 } color={ '#dbdbdb' }/>
          </Icon>
          </Icons>
   
          <DataContent>

          <Controller
          control={control}
          name="produto"
          render={({ field: { onChange, OnBlur, value} }) => (   
            <ContainerControl>
            <ContainerLab>
            <InputLab> Produtos </InputLab>
            </ContainerLab>  
          <ContainerSelect onPress={() => setProdModal(true)}>

          {selectedProd == ''?
          <LabSelect> Selecione por favor </LabSelect> : 
          <LabSelect> {produto.PRODUTO} </LabSelect> }

          <SetSelect>
          <FontAwesomeIcon icon={faCaretDown}  size={ 15 } color={ '#677689' }/>
          </SetSelect>
          
            </ContainerSelect>
            </ContainerControl>
        )}/>

          <Modal         
          visible={prodModal}
          transparent={true}
          animationType="fade"
          onRequestClose={() => setProdModal(false)}>

          <ContainerModal>

          <TopContainer>
          <ContainerSearch>
          <FontAwesomeIcon icon={faMagnifyingGlass} size={ 25 } color={ '#677689' }/>
          </ContainerSearch>                       
          <SearchInput
          placeholder='Buscar Produto'
          name={searchProd}
          onChangeText={(t) => setSearchProd(t)}/>                          
          </TopContainer>
         
        <DataView>

        <TouchableOpacity key={0} style={{ margin: 10 }}  onPress={() => setSelectedProd('')}>

        <DataLab> Selecione por favor </DataLab>

        {selectedProd == ''? <Selected>
        <FontAwesomeIcon icon={faCircleCheck}  size={ 25 } color={ '#8395ab' }/>       
        </Selected> : null}

        </TouchableOpacity>

        <FlatList
            data={produtos}            
            keyExtractor={item => item.CODIGO}
            renderItem={({ item }) => {
              return <TouchableOpacity key={item.CODIGO} style={{ margin: 10 }}  onPress={() => setSelectedProd(item.CODIGO)}>

              <DataLab> Codigo: <DataReplyLab> {item.CODIGO} </DataReplyLab> </DataLab>
              <DataLab> Produto: <DataReplyLab> {item.PRODUTO} </DataReplyLab> </DataLab>
              <DataLab> Preço: <DataReplyLab> {item.PRECOVENDA} </DataReplyLab> </DataLab>
              <DataLab> Codbarra: <DataReplyLab> {item.CODBARRA} </DataReplyLab> </DataLab>
    
              {item.CODIGO == selectedProd? <Selected>
              <FontAwesomeIcon icon={faCircleCheck}  size={ 25 } color={ '#8395ab' }/>       
              </Selected> : null}
    
              </TouchableOpacity>;
            }}/>

        </DataView>
        </ContainerModal>
        <Back onPress={() => setProdModal(false)}>
        <BackLab> Voltar </BackLab>
        </Back>

        </Modal>

        {setores.length >= 2?  

          <Controller
          control={control}
          name="setor"
          render={({ field: { onChange, OnBlur, value} }) => ( 
            <ContainerControl>
            <ContainerLab>
            <InputLab> Setor </InputLab>
            </ContainerLab>     
          <ContainerSelect onPress={() => setSetorModal(true)}>

          {selectedSetor == ''?
          <LabSelect> Selecione por favor </LabSelect> : 
          <LabSelect> {setor.SETOR} </LabSelect> }

          <SetSelect>
          <FontAwesomeIcon icon={faCaretDown}  size={ 15 } color={ '#677689' }/>
          </SetSelect>
  
            </ContainerSelect>
            </ContainerControl>
        )}/>
        : null
      }  

          <Modal         
          visible={setorModal}
          transparent={true}
          animationType="fade"
          onRequestClose={() => setSetorModal(false)}>

          <ContainerModal>

          <TopContainer>
          <ContainerSearch>
          <FontAwesomeIcon icon={faMagnifyingGlass} size={ 25 } color={ '#677689' }/>
          </ContainerSearch>                       
          <SearchInput
          placeholder='Buscar Setor'
          name={searchSetores}
          onChangeText={(t) => setSearchSetores(t)}/>                          
          </TopContainer>
          
        <DataView>

        <DataEstoque>
        {selectedSetor == ''?
          <EstoqueLab> Selecione um setor para visualizar o estoque </EstoqueLab> : 
          <EstoqueLab> Estoque atual: {estoque.ESTOQUE_ATUAL} </EstoqueLab> }
        </DataEstoque>

        <TouchableOpacity key={0} style={{ margin: 10 }}  onPress={() => setSelectedSetor('')}>

        <DataLab> Selecione por favor </DataLab>

        {selectedSetor == ''? <Selected>
        <FontAwesomeIcon icon={faCircleCheck}  size={ 25 } color={ '#8395ab' }/>       
        </Selected> : null}

        </TouchableOpacity>

        

        <FlatList
            data={setores}            
            keyExtractor={item => item.CODIGO}
            renderItem={({ item }) => { 
              return <TouchableOpacity key={item.CODIGO} style={{ margin: 10 }}  onPress={() => setSelectedSetor(item.CODIGO)}>

              <DataLab> Codigo: <DataReplyLab> {item.CODIGO} </DataReplyLab> </DataLab>
              <DataLab> Setor: <DataReplyLab> {item.SETOR} </DataReplyLab> </DataLab>
                      
              {item.CODIGO == selectedSetor? <Selected>
              <FontAwesomeIcon icon={faCircleCheck}  size={ 25 } color={ '#8395ab' }/>       
              </Selected> : null}
    
              </TouchableOpacity>;
            }}/>


        </DataView>
        </ContainerModal>
        <Back onPress={() => setSetorModal(false)}>
        <BackLab> Voltar </BackLab>
        </Back>

        </Modal>     
       

          <Controller
          control={control}
          name="quntd"
          render={({ field: { onChange, OnBlur, value} }) => (
            <ContainerControl>
            <ContainerLab>
            { selectedSetor == ''? <InputLab> Quantidade </InputLab>
           : <InputLab> Quantidade <EstoqueInputLab>
            (1 - {estoque.ESTOQUE_ATUAL}) </EstoqueInputLab> </InputLab> }
            </ContainerLab>      
          <ContainerInput>
          <Input
          name={quntd}
          onChangeText={(t) => setQuntd(t)}
          keyboardType='numeric'
          OnBlur={OnBlur}
          value={value}
          placeholder='Informe quantidade'/>
          </ContainerInput>
          </ContainerControl>
        )}/>

          <Controller
          control={control}
          name="desc"
          render={({ field: { onChange, OnBlur, value} }) => (
            <ContainerControl>
            <ContainerLab>
            <InputLab> Desconto </InputLab>
            </ContainerLab>
          <ContainerRadio>
          <RadioCont>
          <RadioButton
          color='#677689'
          value="prcntg"
          status={ tipoddesconto === 'prcntg' ? 'checked' : 'unchecked' }
          onPress={prcntgRadio} />
          <RadioLab> % </RadioLab>
          </RadioCont>
          <RadioCont>
          <RadioButton
          color='#677689'
          value="reais"
          status={ tipoddesconto === 'reais' ? 'checked' : 'unchecked' }
          onPress={reaisRadio} />
          <RadioLab> R$ </RadioLab>
          </RadioCont>
          </ContainerRadio>
          </ContainerControl>
        )}/>

          <Controller
          control={control}
          name="prcntg"
          render={({ field: { onChange, OnBlur, value} }) => (
            <ContainerControl>
            <ContainerLab>
            <InputLab> % </InputLab>
            </ContainerLab>      
          <ContainerInput>
          <Input
          keyboardType='numeric'
          editable={editprcntg}
          name={prcntg}
          onChangeText={(t) => setPrcntg(t)}
          OnBlur={OnBlur}
          value={value}
          placeholder='desconto em %'/>
          </ContainerInput>
          </ContainerControl>
        )}/>

          <Controller
          control={control}
          name="reais"
          render={({ field: { onChange, OnBlur, value} }) => (
            <ContainerControl>
            <ContainerLab>
            <InputLab> R$ </InputLab>
            </ContainerLab>      
          <ContainerInput>
          <Input
          keyboardType='numeric'
          editable={editreais}
          name={reais}
          onChangeText={(t) => setReais(t)}
          OnBlur={OnBlur}
          value={value}
          placeholder='desconto em R$'/>
          </ContainerInput>
          </ContainerControl>
        )}/> 

          </DataContent>

          <ContainerDisplay>
          <DisplayCont>
           <DisplayLab> Preço(R$): </DisplayLab> 
        { produto.PRECOVENDA === undefined? <ValueLab> 0,00 </ValueLab> :
        <ValueLab> {produto.PRECOVENDA} </ValueLab>  }        
          </DisplayCont>
          <DisplayCont>
          { tipoddesconto === 'reais' ? <DisplayLab> Total(R$): <DisplayLabDesc> - {rxp}% </DisplayLabDesc> </DisplayLab> :
          tipoddesconto === 'prcntg' ?  <DisplayLab> Total(R$): <DisplayLabDesc> - {pxr}R$ </DisplayLabDesc>  </DisplayLab> :
          <DisplayLab> Total(R$): </DisplayLab>  }
           <ValueLab> {valorTotal} </ValueLab> 
          </DisplayCont>
          </ContainerDisplay>

        
         <Save>
         <SaveLab> Proximo </SaveLab>
         </Save>

        </Container>
    );
  };


  export default Atendimento;
