import * as Clipboard from 'expo-clipboard';
import React, { useState, useContext } from "react";
import { ActivityIndicator, FlatList, View, TouchableOpacity } from 'react-native';
import { QueryClient, useQuery, useInfiniteQuery, QueryClientProvider } from "react-query";
import { Container, CartContent, DataLab, DataLimiter, Copy, CopyLab, CopyConteiner, DataReplyLab, Copied, CopiedLab, SetSelect, Select } from "./styles/show";
import { faCaretDown, faCaretUp, faCircle ,faCopy } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome';
import { ProductContent, DataLabC, DataReplyLabC } from "./styles/atendimento";
import { ScrollView } from 'react-native-gesture-handler';
import { ConfigContext } from '../context/config';


function ShowVendas({route}) {
  
  return (  
    <QueryClientProvider client={queryClient}>
      <Conteudo cod={route.params.cod}/>
    </QueryClientProvider>
   
  )
}

const queryClient = new QueryClient()

async function getVendas([cod, baseUrl]) {
  const response = await fetch(
    `http://${baseUrl}/C000074/${cod}`
  );

   return response.json(); 

}

const Conteudo = (props) => {

  const [alertCopy, setAlertCopy] = useState(false);
  const [alertMensag, setAlertMensag] = useState('');
  const [viewProds, setViewProds] = useState(false);
  const { baseUrl } = useContext(ConfigContext)

  const vendas = useQuery( 
    ["vendas", props.cod, baseUrl], () => getVendas([props.cod, baseUrl]), {
      staleTime: 5000, cacheTime: 4000
    },
  );

  const copyItem = ([info, data]) => {
    
    setAlertMensag(info)
    Clipboard.setString(''+data+'')
    setAlertCopy(true)

    setTimeout(() => {
      setAlertCopy(false);
    }, 2000);
       
  };

  const copy = () => {

    setAlertMensag('')
    Clipboard.setString('Código : ' + vendas.data.CODIGO + 
    '\n\nCliente : ' +  vendas.data.cliente.NOME + ' ' + vendas.data.cliente.APELIDO + 
    '\n\nData : ' + vendas.data.DATA +
    '\n\nObs : ' + vendas.data.OBS + 
    '\n\nSubTotal : ' + vendas.data.SUBTOTAL + 
    '\n\nDesconto : ' + vendas.data.DESCONTO + 
    '\n\nAcrescimo : ' + vendas.data.ACRESCIMO +		
    '\n\nTotal : ' + vendas.data.TOTAL ) 
    setAlertCopy(true)

    setTimeout(() => {
      setAlertCopy(false);
    }, 2000);      
  };

  if (vendas.isLoading) {
    return (
      <View style={{flex:1,
      backgroundColor:'#dbdbdb',
      alignItems:'center',
      justifyContent:'center', }}>   
      <ActivityIndicator size="large" color="#677689" />
      </View>
    )
  }
  if (vendas.isError) {
    return (
      <Container>
      <DataLab> Tente Novamente </DataLab>
      </Container>
    )

  }
  if (vendas.isSuccess) {

    //console.log(vendas.data)
    
return (
    <Container>

{alertCopy == true?
      <Copied>
       <CopiedLab> {alertMensag} Copiado </CopiedLab> 
      </Copied> : null }

      <ScrollView>

      <CopyConteiner>
      <Copy onPress={() => copy()}>
      <FontAwesomeIcon icon={faCopy} size={ 25 } color={ '#dbdbdb' }/>
      </Copy>
      </CopyConteiner>

    <DataLimiter>

    <TouchableOpacity onPress={() => copyItem(['Código', vendas.data.CODIGO])}>
    <DataLab> Código: <DataReplyLab> {vendas.data.CODIGO} </DataReplyLab> </DataLab>
    </TouchableOpacity>

    <TouchableOpacity onPress={() => copyItem(['Cliente', vendas.data.cliente.NOME + ' ' + vendas.data.cliente.APELIDO])}>
    <DataLab> Cliente: <DataReplyLab> {vendas.data.cliente.NOME} {vendas.data.cliente.APELIDO} </DataReplyLab> </DataLab>
    </TouchableOpacity>

    <TouchableOpacity onPress={() => copyItem(['Data', vendas.data.DATA])}>
    <DataLab> Data: <DataReplyLab> {vendas.data.DATA} </DataReplyLab> </DataLab>
    </TouchableOpacity>

    {vendas.data.OBS == null || vendas.data.OBS == ""? null :
    <TouchableOpacity onPress={() => copyItem(['Obs', vendas.data.OBS])}>
    <DataLab> Obs: <DataReplyLab> {vendas.data.OBS} </DataReplyLab> </DataLab>
    </TouchableOpacity> }

    <TouchableOpacity onPress={() => copyItem(['Subtotal', vendas.data.SUBTOTAL])}>
    <DataLab> Subtotal: <DataReplyLab> R$ {vendas.data.SUBTOTAL} </DataReplyLab> </DataLab>
    </TouchableOpacity>

    <TouchableOpacity onPress={() => copyItem(['Desconto', vendas.data.DESCONTO])}>
    <DataLab> Desconto: <DataReplyLab> R$ -{vendas.data.DESCONTO} </DataReplyLab> </DataLab>
    </TouchableOpacity>

    <TouchableOpacity onPress={() => copyItem(['Acrescimo', vendas.data.ACRESCIMO])}>
    <DataLab> Acrescimo: <DataReplyLab> R$ +{vendas.data.ACRESCIMO} </DataReplyLab> </DataLab>
    </TouchableOpacity>

    <TouchableOpacity onPress={() => copyItem(['Total', vendas.data.TOTAL])}>
    <DataLab> Total: <DataReplyLab> R$ {vendas.data.TOTAL} </DataReplyLab> </DataLab>
    </TouchableOpacity>

    
    <View>
    { viewProds == false?
    <Select onPress={() => setViewProds(true)}>
    <DataLab> Conteudo da venda </DataLab>
    <SetSelect>
    
    <FontAwesomeIcon icon={faCaretDown} size={ 15 } color={ '#677689' }/> 
    </SetSelect>
    </Select> :
    <Select onPress={() => setViewProds(false)}>
    <DataLab> Conteudo da venda </DataLab>
    <SetSelect>
    
    <FontAwesomeIcon icon={faCaretUp} size={ 15 } color={ '#677689' }/> 
    </SetSelect>
    </Select> }

  { viewProds == true? <CartContent>

{ vendas.data.vendas.map ( (item) => <View key={item.CODIGO}>

<ProductContent>

<DataLabC> Produto: <DataReplyLabC> {item.produto.PRODUTO} </DataReplyLabC> </DataLabC>
<DataLabC> Setor: <DataReplyLabC> {item.setor.SETOR} </DataReplyLabC> </DataLabC>
<DataLabC> Quantidade: <DataReplyLabC> {item.QTDE} </DataReplyLabC> </DataLabC>
<DataLabC> Desconto: <DataReplyLabC> R$ -{item.DESCONTO} </DataReplyLabC> </DataLabC>
<DataLabC> Acrescimo: <DataReplyLabC> R$ +{item.ACRESCIMO} </DataReplyLabC> </DataLabC>
<DataLabC> Total: <DataReplyLabC> {item.TOTAL} </DataReplyLabC> </DataLabC>

</ProductContent>
</View> )}


    </CartContent> : null }
    </View>

    </DataLimiter>

    </ScrollView>

    

    

    </Container>
 
    )
  }
}

export default ShowVendas;
