import styled from 'styled-components/native';

export const Container = styled.View`
width: 100%;
height: 102%;
align-items: center;
background-color: #dbdbdb;
`;

export const ContainerControlCliente = styled.View`
color: #677689;
width: 90%;
height: 100px;
font-size: 20px;
font-weight: bold;
justify-content: center;
`;

export const ContainerLabCliente = styled.View` 
color: #677689;
font-size: 20px;
`;

export const ContainerSelectCliente = styled.TouchableOpacity` 
color: #677689;
position: relative;
width: 100%;
height: 50px;
font-size: 20px;
font-weight: bold;
align-items: center;
border-width: 2px;
border-radius: 2px; 
border-color: #677689;
flex-direction: row;
`;

export const SetSelect = styled.View` 
position: absolute;
margin: 10px;
right: 0;
`;

export const LabSelect = styled.Text` 
color: #677689;
font-size: 15px;
margin-left: 5px;
`;

export const Icons = styled.View` 
width: 100%;
flex-direction: row;
margin-left: 50px;
`;

export const Icon = styled.TouchableOpacity`
background-color: #677689;
color: #dbdbdb;
width: 40px;
height: 40px;
margin: 5px;
border-radius: 2px;
align-items: center;
justify-content: center;
`;

export const Items = styled.ScrollView`
background-color: transparent;
width: 100%;
height: 100%;
margin-bottom: 100px;
`;

export const DataContent = styled.View`
background-color: #dbdbdb;
color: #677689;
width: 90%;
height:305px;
border-color: #677689;
border-top-width: 5px;

`;

export const ContainerControl = styled.View`
color: #677689;
flex-direction: row;
width: 100%;
height: 50px;
font-size: 20px;
font-weight: bold;
justify-content: center;
`;


export const ContainerLab = styled.View` 
color: #677689;
font-size: 20px;
width: 40%;
justify-content: center;
text-align: justify;
`;

export const ContainerSelect = styled.TouchableOpacity` 
color: #677689;
width: 60%;
font-weight: bold;
justify-content: center;
border-left-width: 2px;
border-color: #677689;
`;

export const ContainerRadio = styled.View` 
color: #677689;
width: 60%;
border-left-width: 2px;
border-color: #677689;
flex-direction: row;
`;

export const RadioLab = styled.Text` 
color: #677689;
font-size: 15px;
font-weight: bold;
margin-right: 10px;
margin-top: 5px;
`;

export const RadioCont = styled.View` 
flex-direction: row;
margin-top: 10px;
`;

export const ContainerInput = styled.View`
color: #677689;
width: 60%;
font-weight: bold;
justify-content: center;
border-color: #677689;
border-left-width: 2px;
`;

export const Input = styled.TextInput`
background-color: #dbdbdb;
color: #677689;
width: 100%;
height: 45px;
padding: 10px;
`;


export const InputLab = styled.Text` 
color: #677689;
min-width: 50%;
font-size: 20px;
font-weight: bold;
text-align: left;
`;

export const ContainerDisplay = styled.View`
flex-direction: row;
`;

export const DisplayCont = styled.View`
width: 45%;
height: 80px;
margin: 5px;
justify-content: center;
position: relative;
`;

export const DisplayLab = styled.Text` 
color: #677689;
font-size: 15px;
position: absolute;
top: 0;
font-weight: bold;
`;

export const DisplayLabDesc = styled.Text` 
color: #e95361;
font-size: 15px;
position: absolute;
top: 0;
font-weight: bold;
`;

export const ValueLab = styled.Text` 
color: #677689;
font-size: 30px;
text-align: center;
font-weight: bold;
`;

// Modal 

export const DataView = styled.View`
width: 100%;
margin-top: 80px;
margin-bottom: 10px;
height: 89%;
`;

export const ContainerModal = styled.View`
width: 100%;
height: 94%;
align-items: center;
background-color: #dbdbdb;
position: relative;
`;

export const DataEstoque = styled.View`
width: 100%;
height: 40px;
`;

export const EstoqueLab = styled.Text`
color: #8395ab;
font-size: 15px;
font-weight: bold;
margin: 10px;
`;

export const Back = styled.TouchableOpacity`
background-color: #677689;
width: 100%;
height: 50px;
align-items: center;
justify-content: center;

`;

export const BackLab = styled.Text` 
color: #dbdbdb;
font-size: 15px;
`;

// Save

export const Save = styled.TouchableOpacity`
position: absolute;
bottom: 10px;
width: 100%;
height: 50px;
background-color: #677689;
align-items: center;
justify-content: center;
`;

export const SaveLab = styled.Text` 
color: #dbdbdb;
font-size: 15px;
`;

export const ErrorMensage = styled.Text` 
color: #ff0101;
`;
