import React, { useContext, useState } from "react";
import { View, Button, FlatList, ActivityIndicator, Modal } from 'react-native';
import { Container, Content, BackContainer, BackContent, TopContainer, SearchInput, 
  ContainerSearch, BarCode, DataContent, DataLab, DataReplyLab, DataLimiter, IconsContent, IconContent } from "./styles";
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome';
import { faRightFromBracket, faHouse, faBarcode, faMagnifyingGlass, faEye, faPen } from "@fortawesome/free-solid-svg-icons";
import { QueryClient, useQuery, useInfiniteQuery, QueryClientProvider } from "react-query";
import { AuthenticationContext } from '../context/authentication';
import { ConfigContext } from '../context/config';
import { useNavigation } from '@react-navigation/native';
import Scanner from "./scanner";

function Preco() {
  
  return (  
    <QueryClientProvider client={queryClient}>
      <Conteudo/>
    </QueryClientProvider>
   
  )
}

const queryClient = new QueryClient()

// checar se a api esta online

async function get(baseUrl) {
  const response = await fetch(
    `http://${baseUrl}`
  );

   return response.json(); 

}

// listar produto

async function getProdutos([pageParam, searchParam, baseUrl]) {
  const response = await fetch(
    `http://${baseUrl}/C000025/?s=${searchParam}&page=${pageParam}`
  );
  return response.json();
}

function Conteudo() {
  const { LogOut, user, setUserInfos, userPermissions } = useContext(AuthenticationContext)
  const { baseUrl } = useContext(ConfigContext)
  const navigation = useNavigation(); // navigation
  const [searchText, setSearchText] = useState('');
  const [modalVisible, setModalVisible] = useState(false);

      // query checar se a api esta online
      const check = useQuery( 
        ["check", baseUrl], () => get(baseUrl), {
          staleTime: 5000, cacheTime: 4000
        },
      );

      // query produtos
      const produtos = useInfiniteQuery( 
        ["produtos", searchText, baseUrl], ({ pageParam = 1 }) => getProdutos([pageParam, searchText, baseUrl]), 
        { getNextPageParam: (page) => (page.current_page === page.last_page ? null : page.current_page + 1 ) },
        {  staleTime: 5000, cacheTime: 4000 },
      );

      // load produtos
      const loadMoreProdutos = () => {  if (produtos.hasNextPage) {  produtos.fetchNextPage();  }  };
         
    
      const onCodeScanned = (type, data) => {       
        setModalVisible(false);
        setSearchText(data);
            
      };

      const renderSpinner = () => { return <View style={{flex:1,
        backgroundColor:'#dbdbdb',
        alignItems:'center',
        justifyContent:'center', }}>   
        <ActivityIndicator size="large" color="#677689" />
        </View>;  };

      if (check.isLoading) {
        return (
          <View style={{flex:1,
          backgroundColor:'#dbdbdb',
          alignItems:'center',
          justifyContent:'center', }}>   
          <ActivityIndicator size="large" color="#677689" />
          </View>
        )
      }
      if (check.isError) {
        return (
          <Container>
          <DataLab> Tente Novamente </DataLab>
          </Container>
        )
  
      }
      if (check.isSuccess) {
        
    return (
        <Container>
          <Modal         
          visible={modalVisible}
          transparent={true}
          animationType="fade"
          onRequestClose={() => setModalVisible(false)}>

          <View style={{ flex: 1,
            alignItems: "center",
            justifyContent: "space-around",
            backgroundColor: "lightgrey", }}>
          <Scanner onCodeScanned={onCodeScanned} />
          <Button title="Cancelar" color="#677689" onPress={() => setModalVisible(false)} />
          </View>
          </Modal>

          <TopContainer>
          <ContainerSearch>
          <FontAwesomeIcon icon={faMagnifyingGlass} size={ 25 } color={ '#677689' }/>
          </ContainerSearch>                       
          <SearchInput
          placeholder='Buscar Produto'
          name={searchText}
          onChangeText={(t) => setSearchText(t)}>{searchText}</SearchInput>
          <BarCode onPress={() => setModalVisible(true)}>                               
          <FontAwesomeIcon icon={faBarcode}  size={ 25 } color={ '#dbdbdb' }/>
          </BarCode>                             
          </TopContainer>
            
          <Content Style={{alignItems: 'center' }}>

          {produtos.isSuccess? <FlatList
        onEndReached={loadMoreProdutos}
        onEndReachedThreshold={0.5}
        ListFooterComponent={produtos.isFetching ? renderSpinner : null}
        data={(produtos.data.pages.map((page) => page.data)).flat()}            
        keyExtractor={item => item.CODIGO}
        renderItem={({ item }) => {
          return <DataContent>
               
          <DataLimiter>
          <DataLab> Codigo: <DataReplyLab> {item.CODIGO} </DataReplyLab> </DataLab>
          <DataLab> Produto: <DataReplyLab> {item.PRODUTO} </DataReplyLab> </DataLab>
          <DataLab> Preço: <DataReplyLab> {item.PRECOVENDA} </DataReplyLab> </DataLab>
          </DataLimiter>

          <IconsContent>
          <IconContent onPress={() => navigation.navigate('ShowPreco', {cod: item.CODIGO})}>
          <FontAwesomeIcon icon={faEye} size={ 21 } color={ '#dbdbdb' } />  
          </IconContent>
          {userPermissions.includes('EditarProduto')? 
          <IconContent onPress={() => navigation.navigate('EditPreco', { cod: item.CODIGO })}>
          <FontAwesomeIcon icon={faPen} size={ 15 } color={ '#dbdbdb' } />  
          </IconContent> : null }
          </IconsContent>

          </DataContent>;
        }}/> : <View style={{flex:1,
          backgroundColor:'#dbdbdb',
          alignItems:'center',
          justifyContent:'center', }}>   
          <ActivityIndicator size="large" color="#677689" />
          </View>  }
          
          </Content> 

          <BackContainer>
            <BackContent onPress={() => navigation.navigate('Start')}>             
            <FontAwesomeIcon icon={faHouse} size={ 52 } color={ '#677689' }/>
            </BackContent>
            <BackContent  onPress={() => {LogOut(); navigation.navigate('Start')}}>             
            <FontAwesomeIcon icon={faRightFromBracket} size={ 52 } color={ '#677689' }/>
            </BackContent>
          </BackContainer>
          
        </Container>
    )
          }
  }

  export default Preco;
