import React, { useContext, useState } from "react";
import { FlatList, ActivityIndicator, View } from 'react-native';
import { Container, Content, BackContainer, BackContent, TopContainer, SearchInput, 
  ContainerSearch, Filter, DataContent, DataLab, DataReplyLab, DataLimiter, IconsContent, IconContent } from "./styles";
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome';
import { ConfigContext } from '../context/config'
import { faRightFromBracket, faHouse, faArrowUpAZ, faMagnifyingGlass, faEye, faPen } from "@fortawesome/free-solid-svg-icons";
import { QueryClient, useQuery, useInfiniteQuery, QueryClientProvider } from "react-query";
import { useNavigation } from '@react-navigation/native';
import { AuthenticationContext } from '../context/authentication'

function Cliente() {
  
  return (  
    <QueryClientProvider client={queryClient}>
      <Conteudo/>
    </QueryClientProvider>
   
  )
}

const queryClient = new QueryClient()

// checar se a api esta online

async function get(baseUrl) {
  const response = await fetch(
    `http://${baseUrl}`
  );

   return response.json(); 

}

// listar produto

async function getClientes([pageParam, searchParam, baseUrl]) {
  const response = await fetch(
    `http://${baseUrl}/C000007?s=${searchParam}&page=${pageParam}`
  );
   return response.json(); 
}

function Conteudo() {
  const { LogOut, user, setUserInfos, userPermissions } = useContext(AuthenticationContext)
  const navigation = useNavigation(); // navigation
  const { baseUrl, appInfo, setInfoApp } = useContext(ConfigContext)
  const [searchText, setSearchText] = useState('');

        // query checar se a api esta online
        const check = useQuery( 
          ["check", baseUrl], () => get(baseUrl), {
            staleTime: 2000, cacheTime: 10
          },
        );
  
        // query clientes
        const clientes = useInfiniteQuery(    
          ["clientes", searchText, baseUrl], ({ pageParam = 1 }) => getClientes([pageParam, searchText, baseUrl]), 
          { getNextPageParam: (page) => (page.current_page === page.last_page ? null : page.current_page + 1 ) },
          { staleTime: 2000, cacheTime: 10 },
        );

        // load clientes
        const loadMoreClientes = () => {  if (clientes.hasNextPage) {  clientes.fetchNextPage();  }  };

        const renderSpinner = () => { return <View style={{flex:1,
        backgroundColor:'#dbdbdb',
        alignItems:'center',
        justifyContent:'center', }}>   
        <ActivityIndicator size="large" color="#677689" />
        </View>;  };

      if (check.isLoading) {
        return (
          <View style={{flex:1,
          backgroundColor:'#dbdbdb',
          alignItems:'center',
          justifyContent:'center', }}>   
          <ActivityIndicator size="large" color="#677689" />
          </View>
        )
      }
      if (check.isError) {
        return (
          <Container>
          <DataLab> Tente Novamente... </DataLab>
          </Container>
        )
  
      }
      if (check.isSuccess) {
        
    return (
        <Container>     
          <TopContainer>
          <ContainerSearch>
          <FontAwesomeIcon icon={faMagnifyingGlass} size={ 25 } color={ '#677689' }/>
          </ContainerSearch>                       
          <SearchInput
          placeholder='Buscar Cliente'
          name={searchText}
          onChangeText={(t) => setSearchText(t)}/>                            
          </TopContainer>
            
          <Content Style={{alignItems: 'center' }}>

          {clientes.isSuccess? <FlatList
        onEndReached={loadMoreClientes}
        onEndReachedThreshold={0.5}
        ListFooterComponent={clientes.isFetching ? renderSpinner : null}
        data={(clientes.data.pages.map((page) => page.data)).flat()}    
        keyExtractor={(item) => item.CODIGO}    
        renderItem={({ item }) => {
          return <DataContent>
               
          <DataLimiter>
          <DataLab> Codigo: <DataReplyLab> {item.CODIGO} </DataReplyLab> </DataLab>
          <DataLab> Cliente: <DataReplyLab> {item.NOME} {item.APELIDO} </DataReplyLab> </DataLab>
          <DataLab> CPF: <DataReplyLab> {item.CPF} </DataReplyLab> </DataLab>
          </DataLimiter>

          <IconsContent>
          <IconContent onPress={() => navigation.navigate('ShowCliente', { cod: item.CODIGO, })}>
          <FontAwesomeIcon icon={faEye} size={ 21 } color={ '#dbdbdb' } />  
          </IconContent>
          {userPermissions.includes('EditarCliente')? 
          <IconContent onPress={() => navigation.navigate('EditCliente', { cod: item.CODIGO, })}>
          <FontAwesomeIcon icon={faPen} size={ 15 } color={ '#dbdbdb' } />  
          </IconContent> : null }
          </IconsContent>

          </DataContent>;
        }}/> : <View style={{flex:1,
          backgroundColor:'#dbdbdb',
          alignItems:'center',
          justifyContent:'center', }}>   
          <ActivityIndicator size="large" color="#677689" />
          </View>  }
          
          </Content> 

          <BackContainer>
            <BackContent onPress={() => navigation.navigate('Start')}>             
            <FontAwesomeIcon icon={faHouse} size={ 52 } color={ '#677689' }/>
            </BackContent>
            <BackContent  onPress={() => {LogOut(); navigation.navigate('Start')}}>             
            <FontAwesomeIcon icon={faRightFromBracket} size={ 52 } color={ '#677689' }/>
            </BackContent>
          </BackContainer>
          
        </Container>
    )
      }
  }

  export default Cliente;
