import React, { useEffect, useState } from "react";
import { StatusBar } from 'expo-status-bar';
import { View, Button, FlatList, Alert, Modal } from 'react-native';
import { Container, Content, BackContainer, BackContent, TopContainer, SearchInput, 
  ContainerSearch, Filter, BarCode, DataContent, DataLab, DataReplyLab, DataLimiter, IconsContent, IconContent } from "./styles";
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome';
import { faRightFromBracket, faHouse, faArrowUpAZ, faBarcode, faMagnifyingGlass, faEye, faPen } from "@fortawesome/free-solid-svg-icons";
import * as Clipboard from 'expo-clipboard';
import Scanner from "./scanner";

const Preco = ({ navigation }) => {

  const [searchText, setSearchText] = useState('');
  const [produtos, setProdutos] = useState([]); 
  useEffect(() => {
    fetch('http://192.168.18.50/SmartMobile_Api/public/api/produtos', {
      method: 'GET',
      headers: {
        'Accept': 'application/json'
      }
    })
       .then(response => response.json())
       .then(data => {
        
        if(searchText === '') {
          setProdutos(data.data)
         } 
         
         if(searchText !== '') {
           setProdutos(
             data.data.filter(
               (prod) => 
             prod.codbarra.indexOf(searchText) > -1 || prod.produto.toLowerCase().indexOf(searchText.toLowerCase()) > -1
             )
           );
             }
       })
      },[searchText])

      const handleOrderClick = () => {
        let newList = [...produtos];
    
        newList.sort((a, b) => (a.produto > b.produto ? 1 : b.produto > a.produto ? -1 : 0));
    
        setProdutos(newList);
      };

      const [modalVisible, setModalVisible] = React.useState(false);
      const [type, setType] = React.useState("");
      const [data, setData] = React.useState("");
    
      const onCodeScanned = (type, data) => {
        setType(type);
        setData(data);
        setModalVisible(false);    
      };

    return (
        <Container>
          <Modal         
          visible={modalVisible}
          transparent={true}
          animationType="fade"
          onRequestClose={() => setModalVisible(false)}>

          <View style={{ flex: 1,
            alignItems: "center",
            justifyContent: "space-around",
            backgroundColor: "lightgrey", }}>
          <Scanner onCodeScanned={onCodeScanned} />
          <Button title="Cancelar" color="#677689" onPress={() => setModalVisible(false)} />
          </View>
          </Modal>

          <StatusBar backgroundColor="#677689"/>         
          <TopContainer>
          <ContainerSearch>
          <FontAwesomeIcon icon={faMagnifyingGlass} size={ 25 } color={ '#677689' }/>
          </ContainerSearch>                       
          <SearchInput
          placeholder='Buscar produto'
          name={searchText}
          onChangeText={(t) => setSearchText(t)}
          >{data}</SearchInput>
          <Filter onPress={handleOrderClick}>                               
          <FontAwesomeIcon icon={faArrowUpAZ}  size={ 25 } color={ '#677689' }/>
          </Filter>
          <BarCode onPress={() => setModalVisible(true)}>                               
          <FontAwesomeIcon icon={faBarcode}  size={ 25 } color={ '#dbdbdb' }/>
          </BarCode>                              
          </TopContainer>
            
          <Content Style={{alignItems: 'center' }}>

            <FlatList
            data={produtos}
            renderItem={({item}) =>
            <DataContent key={item.id}>
            <DataLimiter>
            <DataLab> Código: <DataReplyLab>{item.id}</DataReplyLab> </DataLab>               
            <DataLab> Produto: <DataReplyLab> {item.produto} </DataReplyLab> </DataLab>
            <DataLab> Preço: <DataReplyLab> {item.preco} </DataReplyLab> </DataLab>           
            </DataLimiter>
            <IconsContent>
            <IconContent onPress={() =>
            Alert.alert(
              "Visualizar Produto",
              'Código : ' + item.id + 
              '\n\nProduto : ' + item.produto + 
              '\n\nUnidade : ' + item.unidade + 
              '\n\nPreço : ' + item.preco +
              '\n\nEstoque : ' + item.estoque + 
              '\n\nCodBarra : ' + item.codbarra ,
              [
                { text: "Copiar", onPress: async () => {
                  await Clipboard.setString('Código : ' + item.id + 
                  '\n\nProduto : ' + item.produto + 
                  '\n\nUnidade : ' + item.unidade + 
                  '\n\nPreço : ' + item.preco +
                  '\n\nEstoque : ' + item.estoque + 
                  '\n\nCodBarra : ' + item.codbarra);
                  }},
                { text: "OK", onPress: () => console.log("OK") }
              ]
            )}>
            <FontAwesomeIcon icon={faEye} size={ 21 } color={ '#dbdbdb' } />  
            </IconContent>
            <IconContent onPress={() => navigation.navigate('EditPreco', {id: item.id, })}>
            <FontAwesomeIcon icon={faPen} size={ 15 } color={ '#dbdbdb' } />  
            </IconContent>
            </IconsContent>
            </DataContent>
          }/>
          
          </Content>

          <BackContainer>
            <BackContent onPress={() => navigation.navigate('Home')}>             
            <FontAwesomeIcon icon={faHouse} size={ 52 } color={ '#677689' }/>
            </BackContent>
            <BackContent onPress={() => navigation.navigate('Login')}>             
            <FontAwesomeIcon icon={faRightFromBracket} size={ 52 } color={ '#677689' }/>
            </BackContent>
          </BackContainer>
          
        </Container>
    )
  }

  export default Preco;
