import React from 'react';
import { useFonts } from 'expo-font';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';
import { Container, Smartmobile, PurpleCircle, PurpleBar, Inputs, Input } from "./styles/Login";

const Login = () => {

  const [user, onChangeText] = React.useState("Usuário");
  const [pass, onChangeNumber] = React.useState("Senha");

  const [loaded] = useFonts({
    MajorMonoDisplay: require('./fonts/MajorMonoDisplay-Regular.ttf'),
  });
  
  if (!loaded) {
    return null;
  }

    return (
        <Container>
          <PurpleBar>
          </PurpleBar>
          <PurpleCircle>
          </PurpleCircle>  
         <Smartmobile style={{ fontFamily: 'MajorMonoDisplay' }}> SmartMobile </Smartmobile>
         <Inputs>
         <Input placeholder="Usuário"> </Input>
         <Input placeholder="Senha"> </Input>
         </Inputs> 
        </Container> 
    );
  }
  
  export default Login;
