      OnBlur={OnBlur}
      value={value}
      placeholder='Informe sua senha'>{inputsenha}</InputSenha>
       
      </ContainerInputSenha>
    )}/>

    { informSenhaMensag == true? <ErrorMensage>desconto no permitido, senha requerida</ErrorMensage> : null }
    { erroSenhaMensag == true? <ErrorMensage>desconto no permitido, senha errada</ErrorMensage> : null }

        <Confirm onPress={SenhaCheck}> 
        <FontAwesomeIcon icon={faCircleCheck}  size={ 30 } color={ '#d9d9d9' }/>  
        </Confirm>


        </SenhaContent>


        </ContainerVendaModal>
        

      </Modal>

      {/* Senha Final Modal */}


             <Modal         
      visible={senhaFinalModal}
      transparent={true}
      animationType="fade"
      statusBarTranslucent={true} 
      onRequestClose={() => setSenhaFinalModal(false)}>

        <ContainerVendaModal>

        <SenhaContent>

        <Close onPress={() => setSenhaFinalModal(false)}>       
        <FontAwesomeIcon icon={faCircleXmark}  size={ 25 } color={ '#e95361' }/>
        </Close>

      <Controller
      control={control}
      name="inputsenha"
      render={({ field: { onChange, OnBlur, value} }) => (     
      <ContainerInputSenha>
      <InputLab> Senha Mobile: </InputLab>
    
      <InputSenha
      name={inputsenha}
      onChangeText={(t) => SetInputsenha(t)}
      OnBlur={OnBlur}
      value={value}
      placeholder='Informe sua senha'>{inputsenha}</InputSenha>
       
      </ContainerInputSenha>
    )}/>

    { informSenhaMensag == true? <ErrorMensage>desconto no permitido, senha requerida</ErrorMensage> : null }
    { erroSenhaMensag == true? <ErrorMensage>desconto no permitido, senha errada</ErrorMensage> : null }

        <Confirm onPress={SenhaCheckFinal}> 
        <FontAwesomeIcon icon={faCircleCheck}  size={ 30 } color={ '#d9d9d9' }/>  
        </Confirm>


        </SenhaContent>


        </ContainerVendaModal>
        

      </Modal>
 

    </Container>
     ) 
    
  }
    
  };

  export default Atendimento;
