import styled from 'styled-components/native';

export const Container = styled.View`
width: 100%;
height: 100%;
align-items: center;
justify-content: center;
background-color: #dbdbdb;
`;

export const PurpleCircle = styled.View`
position: absolute;
background-color: #677689;
top: 220px;
width: 150%;
height: 100%;
border-radius: 300px;
`;

export const Smartmobile = styled.Text`
position: absolute;
color: #677689;
font-size: 40px;
top: 100px;
`;

export const Inputs = styled.View`
position: relative;
width: 100%;
height: 100%;
align-items: center;
justify-content: center;
`;

export const Input = styled.TextInput` 
background-color: #dbdbdb;
color: #677689;
width: 90%;
height: 45px;
margin-bottom: 15px;
border-radius: 20px;
border-width: 1px;
text-align: center;
border-color: #ddd;
border-bottom-width: 0;
shadow-color: #000;
shadow-opacity: 0.8;
shadow-radius: 2px;
elevation: 9;
`;

export const Button1 = styled.View`
background-color: #535f6e;
border-radius: 2px;
width:80px;
height: 40px;
align-items: center;
justify-content: center;
top: 10px;
`;

export const Button2 = styled.View`
position: absolute;
background-color: #00000040;
border-radius: 2px;
width:80px;
height: 40px;
align-items: center;
justify-content: center;
left: 3px;
bottom: 3px;
`;

export const Entrar = styled.Text`
color: #ddd;
font-size: 20px;
font-weight: bold;
bottom: 2px;
left: 2px;
`;
