import { Container, Smartmobile, PurpleCircle, PurpleBar, Inputs,
 KeyboardActive, Input } from "./styles/Login";

const Login = () => {

  const [loaded] = useFonts({
    MajorMonoDisplay: require('./fonts/MajorMonoDisplay-Regular.ttf'),
  });
  
  if (!loaded) {
    return null;
  }

    return (
        <Container>
          <PurpleBar>
          </PurpleBar>
          <PurpleCircle>
          </PurpleCircle>  
         <Smartmobile style={{ fontFamily: 'MajorMonoDisplay' }}> SmartMobile </Smartmobile>
