         <Input placeholder=" UsuÃ¡rio" underlineColorAndroid='transparent'> </Input>
         <Input placeholder=" Senha" underlineColorAndroid='transparent'> </Input>
         </KeyboardActive>
        </Container> 
    );
  }
  
  const styles = StyleSheet.create({
    active: {
      position: "relative",
      width: "100%",
      height: "100%",
      alignItems: center,
      justifyContent: center,
      bottom: 70,
    }
});

  export default Login;
